  			var e = eStart;
  			do {
  				e.Lface = newLface;
  				e = e.Lnext;
  			} while(e !== eStart);

  			/* delete from circular doubly-linked list */
  			var fPrev = fDel.prev;
  			var fNext = fDel.next;
  			fNext.prev = fPrev;
  			fPrev.next = fNext;
