import GlobalVariables from './js/globalvariables'
import Molecule from './js/molecules/molecule.js'
import GitHubMolecule from './js/molecules/githubmolecule.js'
import {cmenu, showGitHubSearch} from './js/NewMenu.js'


GlobalVariables.canvas = document.querySelector('canvas')
GlobalVariables.c = GlobalVariables.canvas.getContext('2d')
GlobalVariables.runMode = window.location.href.includes('run') //Check if we are using the run mode based on url

GlobalVariables.canvas.width = window.innerWidth
GlobalVariables.canvas.height = window.innerHeight/2.5

// Event Listeners
/** 
 * The cansvas on which the atoms are placed.
 * @type {object}
 */
let flowCanvas = document.getElementById('flow-canvas')

flowCanvas.addEventListener('mousemove', event => {
    GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(molecule => {

        molecule.clickMove(event.clientX,event.clientY)    
    })
})

flowCanvas.addEventListener('mousedown', event => {

    //every time the mouse button goes down
    
    var isRightMB
    if ("which" in event){  // Gecko (Firefox), WebKit (Safari/Chrome) & Opera
        isRightMB = event.which == 3
    }
    else if ("button" in event){  // IE, Opera 
        isRightMB = event.button == 2
    }
    if(isRightMB){
        return
    }

    var clickHandledByMolecule = false

    GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(molecule => {
        
        if (molecule.clickDown(event.clientX,event.clientY,clickHandledByMolecule) == true){
            clickHandledByMolecule = true
        }

    })
    
    //Draw the selection box
    if (!clickHandledByMolecule){
        GlobalVariables.currentMolecule.placeAtom({
            parentMolecule: GlobalVariables.currentMolecule, 
            x: GlobalVariables.pixelsToWidth(event.clientX),
            y: GlobalVariables.pixelsToHeight(event.clientY),
            parent: GlobalVariables.currentMolecule,
            name: 'Box',
            atomType: 'Box'
        }, null, GlobalVariables.availableTypes)
    }
    
    if(!clickHandledByMolecule){
        GlobalVariables.currentMolecule.backgroundClick() 
    }
    else{
        GlobalVariables.currentMolecule.selected = false
    }
    
    //hide the menu if it is visible
    if (!document.querySelector('#circle-menu1').contains(event.target)) {
        cmenu.hide()
    }
    //hide search menu if it is visible
    if (!document.querySelector('#canvas_menu').contains(event.target)) {
        const menu = document.querySelector('#canvas_menu')
        menu.classList.add('off')
        menu.style.top = '-200%'
        menu.style.left = '-200%'
    }
    //hide the menu if it is visible
    if (!document.querySelector('#straight_menu').contains(event.target)) {
        closeTopMenu()
        let options = document.querySelectorAll('.option')
        Array.prototype.forEach.call(options, a => {
            a.classList.remove("openMenu") 
        })
    }
    
})

flowCanvas.addEventListener('dblclick', event => {
    //every time the mouse button goes down    
    var clickHandledByMolecule = false
    
    GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(molecule => {
        if (molecule.doubleClick(event.clientX,event.clientY) == true){
            clickHandledByMolecule = true
        }
    })
    
    if (clickHandledByMolecule == false){
        console.warn('double click menu open not working in flowDraw.js')
        //showmenu(event);
    }
})


document.addEventListener('mouseup',(e)=>{

    if(e.srcElement.tagName.toLowerCase() !== ("textarea")
        && e.srcElement.tagName.toLowerCase() !== ("input")
        && e.srcElement.tagName.toLowerCase() !== ("select")
        &&(!e.srcElement.isContentEditable)){
        //puts focus back into mainbody after clicking button
        document.activeElement.blur()
        document.getElementById("mainBody").focus()
    }
})


flowCanvas.addEventListener('mouseup', event => {
    //every time the mouse button goes up
    GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(molecule => {
        molecule.clickUp(event.clientX,event.clientY)      
    })
    GlobalVariables.currentMolecule.clickUp(event.clientX,event.clientY)      
})


/** 
* Array containing selected atoms to copy or delete
* @type {array}
*/
window.addEventListener('keydown', e => {
    //Prevents default behavior of the browser on canvas to allow for copy/paste/delete
    if(e.srcElement.tagName.toLowerCase() !== ("textarea")
        && e.srcElement.tagName.toLowerCase() !== ("input")
        &&(!e.srcElement.isContentEditable)
        && ['c','v','Backspace'].includes(e.key)){
        e.preventDefault()
    }

    if (document.activeElement.id == "mainBody"){
        if (e.key == "Backspace" || e.key == "Delete") {
            GlobalVariables.atomsSelected = []
            //Adds items to the  array that we will use to delete
            GlobalVariables.currentMolecule.copy()
            GlobalVariables.atomsSelected.forEach(item => {
                GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(nodeOnTheScreen => {
                    if(nodeOnTheScreen.uniqueID == item.uniqueID){
                        nodeOnTheScreen.deleteNode()
                    }
                })
            })
        }    

        /** 
        * Object containing letters and values used for keyboard shortcuts
        * @type {object?}
        */ 
        var shortCuts = {
            a: "Assembly",
            b: "ShrinkWrap",//>
            c: "Copy",
            d: "Difference",
            e: "Extrude",
            g: "GitHub", // Not working yet
            i: "Input",
            j: "Translate", 
            k: "Rectangle",
            l: "Circle",
            m: "Molecule",
            s: "Save", 
            v: "Paste",
            x: "Equation",
            y: "Code", //is there a more natural code letter? can't seem to prevent command t new tab behavior
            z: "Undo" //saving this letter 
        }

        //Copy /paste listeners
        if (e.key == "Control" || e.key == "Meta") {
            GlobalVariables.ctrlDown = true
        }  

        if (GlobalVariables.ctrlDown && shortCuts.hasOwnProperty([e.key])) {
            
            e.preventDefault()
            //Copy & Paste
            if (e.key == "c") {
                GlobalVariables.atomsSelected = []
                GlobalVariables.currentMolecule.copy()
            }
            if (e.key == "v") {
                GlobalVariables.atomsSelected.forEach(item => {
                    let newAtomID = GlobalVariables.generateUniqueID()
                    item.uniqueID = newAtomID
                    GlobalVariables.currentMolecule.placeAtom(item, true)    
                })   
            }
            //Save project
            if (e.key == "s") {
                GlobalVariables.gitHub.saveProject()
            }
            //Opens menu to search for github molecule
            if (e.key == "g") {
                showGitHubSearch()
            }
            
            else { 

                GlobalVariables.currentMolecule.placeAtom({
                    parentMolecule: GlobalVariables.currentMolecule, 
                    x: 0.5,
                    y: 0.5,
                    parent: GlobalVariables.currentMolecule,
                    atomType: `${shortCuts[e.key]}`,
                    uniqueID: GlobalVariables.generateUniqueID()
                }, true)
            }
            
        }
    }
    //every time a key is pressed
    GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(molecule => {  
        molecule.keyPress(e.key)      
    })
   
})

window.addEventListener('keyup', e => {
    if (e.key == "Control" || e.key == "Meta") {
        GlobalVariables.ctrlDown = false
    }
})

/* Button to open top menu */
document.getElementById('straight_menu').addEventListener('mousedown', () => {
    openTopMenu()
}) 

/**
 * Checks if menu is open and changes class to trigger hiding of individual buttons
 */ 
function openTopMenu(){

    document.querySelector('#toggle_wrap').style.display = "inline"
    let options = document.querySelectorAll('.option')
    var step = -150
    Array.prototype.forEach.call(options, a => {
        if (a.classList.contains("openMenu")){
            closeTopMenu() 
            a.classList.remove("openMenu")
        }
        else{
            a.classList.add("openMenu")
            a.style.transition = `transform 0.5s`
            a.style.transform = `translateX(${step}%)` 
            step-=100
            document.getElementById('goup_top').style.visibility = "hidden"
        }
    })
}

/**
 * Closes main menu on background click or on button click if open
 */
function closeTopMenu(){
    document.querySelector('#toggle_wrap').style.display = "inline"
    let options = document.querySelectorAll('.option')
    var step = 0
    document.getElementById('goup_top').style.visibility = "visible"
    Array.prototype.forEach.call(options, a => {
        a.style.transition = `transform 0.5s`
        a.style.transform = `translateX(${step}%)`              
    })  
}


/**
 * Top Button menu event listeners if not in run mode
 */ 
if (!GlobalVariables.runMode){
    
    let githubButton = document.getElementById('github_top')
    githubButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.openGitHubPage()
    })
    let otherProjectsButton = document.getElementById('projectmenu_top')
    otherProjectsButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.showProjectsToLoad()
    })
    let shareButton = document.getElementById('share_top')
    shareButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.shareOpenedProject()
    })
    let bomButton = document.getElementById('bom_top')
    bomButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.openBillOfMaterialsPage()
    })
    let readButton = document.getElementById('read_top')
    readButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.openREADMEPage()
    })
    let saveButton = document.getElementById('save_top')
    saveButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.saveProject()
    })
    let parentButton = document.getElementById('goup_top')
    parentButton.addEventListener('mousedown', () => {
        if(!GlobalVariables.currentMolecule.topLevel){
            GlobalVariables.currentMolecule.goToParentMolecule()  
        }
    })
    let deleteButton = document.getElementById('delete_top')
    deleteButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.deleteProject() 
    })
    let pullButton = document.getElementById('pull_top')
    pullButton.addEventListener('mousedown', () => {
        GlobalVariables.gitHub.makePullRequest() 
    })
}

//Add viewer bar which lets you turn on and off things like wireframe view
/**
 * Contains the check boxes to hide and show the display attributes
 */ 
let viewerBar = document.querySelector('#viewer_bar')
/**
 * The up arrow for going up one level
 */ 
let arrowUpMenu = document.querySelector('#arrow-up-menu')


/** 
* A flag to indicate if the grid should be displayed
* @type {boolean}
*/
var displayGrid = true
/** 
* A flag to indicate if the axis should be displayed
* @type {boolean}
*/
var displayAxis = true

/**
 * Creates the checkbox hidden menu when viewer is active
 */ 
function checkBoxes(){
    let viewerBar = document.querySelector('#viewer_bar')   
    viewerBar.classList.add('slidedown')

    //Grid display html element
    var gridDiv = document.createElement('div')
    viewerBar.appendChild(gridDiv)
    gridDiv.setAttribute('id', 'gridDiv')
    var gridCheck = document.createElement('input')
    gridDiv.appendChild(gridCheck)
    gridCheck.setAttribute('type', 'checkbox')
    gridCheck.setAttribute('id', 'gridCheck')
    gridDiv.setAttribute('style', 'float:right;')
           
    if (displayGrid){
        gridCheck.setAttribute('checked', 'true')
    }

    var gridCheckLabel = document.createElement('label')
    gridDiv.appendChild(gridCheckLabel)
    gridCheckLabel.setAttribute('for', 'gridCheck')
    gridCheckLabel.setAttribute('style', 'margin-right:1em;')
    gridCheckLabel.textContent= "Grid"
    gridCheckLabel.setAttribute('style', 'user-select: none;')


    gridCheck.addEventListener('change', event => {
        if(event.target.checked){
            displayGrid = true
            this.writeToDisplay(this.displayedGeometry)

        }
        else{
            displayGrid = false
            this.writeToDisplay(this.displayedGeometry)
        }
    })

    //Axes Html

    var axesDiv = document.createElement('div')
    viewerBar.appendChild(axesDiv)
    var axesCheck = document.createElement('input')
    axesDiv.appendChild(axesCheck)
    axesCheck.setAttribute('type', 'checkbox')
    axesCheck.setAttribute('id', 'axesCheck')
            
    if (displayAxis){
        axesCheck.setAttribute('checked', 'true')
    }

    var axesCheckLabel = document.createElement('label')
    axesDiv.appendChild(axesCheckLabel)
    axesCheckLabel.setAttribute('for', 'axesCheck')
    axesCheckLabel.setAttribute('style', 'margin-right:1em;')
    axesDiv.setAttribute('style', 'float:right;')
    axesCheckLabel.textContent= "Axes"
    axesCheckLabel.setAttribute('style', 'user-select: none;')

    axesCheck.addEventListener('change', event => {
        if(event.target.checked){
            displayAxis = true

            this.writeToDisplay(this.displayedGeometry)
        }
        else{
            displayAxis = false
            this.writeToDisplay(this.displayedGeometry)
        }
    })
    
    
    //Display faces
    var facesDiv = document.createElement('div')
    viewerBar.appendChild(facesDiv)
    var facesCheck = document.createElement('input')
    facesDiv.appendChild(facesCheck)
    facesCheck.setAttribute('type', 'checkbox')
    facesCheck.setAttribute('id', 'facesCheck')
    
    facesCheck.setAttribute('checked', 'true')
    
    var facesCheckLabel = document.createElement('label')
    facesDiv.appendChild(facesCheckLabel)
    facesCheckLabel.setAttribute('for', 'facesCheck')
    facesCheckLabel.setAttribute('style', 'margin-right:1em;')
    facesDiv.setAttribute('style', 'float:right;')
    facesCheckLabel.textContent= "Faces"
    facesCheckLabel.setAttribute('style', 'user-select: none;')

    facesCheck.addEventListener('change', event => {
        if(event.target.checked){
            GlobalVariables.displayTriangles = true
            GlobalVariables.writeToDisplay(GlobalVariables.displayedPath)
        }
        else{
            GlobalVariables.displayTriangles = false
            GlobalVariables.writeToDisplay(GlobalVariables.displayedPath)
        }
    })
    
    //Display edges
    var edgesDiv = document.createElement('div')
    viewerBar.appendChild(edgesDiv)
    var edgesCheck = document.createElement('input')
    edgesDiv.appendChild(edgesCheck)
    edgesCheck.setAttribute('type', 'checkbox')
    edgesCheck.setAttribute('id', 'edgesCheck')
    
    edgesCheck.setAttribute('checked', 'true')
    
    var edgesCheckLabel = document.createElement('label')
    edgesDiv.appendChild(edgesCheckLabel)
    edgesCheckLabel.setAttribute('for', 'edgesCheck')
    edgesCheckLabel.setAttribute('style', 'margin-right:1em;')
    edgesDiv.setAttribute('style', 'float:right;')
    edgesCheckLabel.textContent= "Edges"
    edgesCheckLabel.setAttribute('style', 'user-select: none;')

    edgesCheck.addEventListener('change', event => {
        if(event.target.checked){
            GlobalVariables.displayEdges = true
            GlobalVariables.writeToDisplay(GlobalVariables.displayedPath)
        }
        else{
            GlobalVariables.displayEdges = false
            GlobalVariables.writeToDisplay(GlobalVariables.displayedPath)
        }
    })
    
    //Display wireframe
    var wireframeDiv = document.createElement('div')
    viewerBar.appendChild(wireframeDiv)
    var wireframeCheck = document.createElement('input')
    wireframeDiv.appendChild(wireframeCheck)
    wireframeCheck.setAttribute('type', 'checkbox')
    wireframeCheck.setAttribute('id', 'wireframeCheck')
    
    var wireframeCheckLabel = document.createElement('label')
    wireframeDiv.appendChild(wireframeCheckLabel)
    wireframeCheckLabel.setAttribute('for', 'wireframeCheck')
    wireframeCheckLabel.setAttribute('style', 'margin-right:1em;')
    wireframeDiv.setAttribute('style', 'float:right;')
    wireframeCheckLabel.textContent= "Wireframe"
    wireframeCheckLabel.setAttribute('style', 'user-select: none;')

    wireframeCheck.addEventListener('change', event => {
        if(event.target.checked){
            GlobalVariables.displayWireframe = true
            GlobalVariables.writeToDisplay(GlobalVariables.displayedPath)
        }
        else{
            GlobalVariables.displayWireframe = false
            GlobalVariables.writeToDisplay(GlobalVariables.displayedPath)
        }
    })
}

document.getElementById('viewerContext').addEventListener('mouseenter', () => {
    if(viewerBar.innerHTML.trim().length == 0){
        checkBoxes()
    }
})

/** 
* A flag to indicate if the startTimer event has already fired
* @type {boolean}
*/
var evtFired = false
var g_timer

/**
 * Starts the timer to retract the menu
 */ 
function startTimer(){
    g_timer = setTimeout(function() {
        if (!evtFired) {
            viewerBar.classList.remove("slideup")
            viewerBar.classList.add('slidedown')  
        }
    }, 2000)
}

arrowUpMenu.addEventListener('mouseenter', () =>{
    clearTimeout(g_timer)
    viewerBar.classList.remove("slidedown")
    viewerBar.classList.add('slideup')   
})
viewerBar.addEventListener('mouseleave', () =>{
    evtFired = false
    viewerBar.classList.remove("slideup")
    viewerBar.classList.add('slidedown')   
})
viewerBar.addEventListener('mouseenter', () =>{
    evtFired = true
    viewerBar.classList.remove("slidedown")
    viewerBar.classList.add('slideup')   
})
arrowUpMenu.addEventListener('mouseleave', () =>{
    startTimer()
})


// Implementation
/**
 * Runs once when the program begins to initialize variables.
 */ 
function init() {
    if(!GlobalVariables.runMode){ //If we are in CAD mode load an empty project as a placeholder
        GlobalVariables.currentMolecule = new Molecule({
            x: 0, 
            y: 0, 
            topLevel: true, 
            name: 'Maslow Create',
            atomType: 'Molecule',
            uniqueID: GlobalVariables.generateUniqueID()
        })
    }
    else{
        var ID = window.location.href.split('?')[1]
        
        //Have the current molecule load it
        if(typeof ID != undefined){
            GlobalVariables.currentMolecule = new GitHubMolecule({
                projectID: ID,
                topLevel: true
            })
            GlobalVariables.topLevelMolecule = GlobalVariables.currentMolecule
            
            //This is used because window.ask takes some time to load so we need to wait for it. This sets up a callback which will be called in jsxcad.js once window.ask exists
            window.askSetupCallback = () => {
                GlobalVariables.topLevelMolecule.loadProjectByID(ID).then( ()=> {
                    GlobalVariables.topLevelMolecule.backgroundClick()
                })
            }
        }
    }
    window.addEventListener('resize', () => { onWindowResize() }, false)

    onWindowResize()
    animate()
}

/**
 * Handles the window's resize behavior when the browser size changes.
 */ 
function onWindowResize() {

    GlobalVariables.canvas.width = window.innerWidth

    //reset screen parameters 
    if(!GlobalVariables.runMode){
        document.querySelector('.flex-parent').setAttribute('style','height:'+ (window.innerHeight - GlobalVariables.canvas.height)+'px')
    }else{
        document.querySelector('.flex-parent').setAttribute('style','height:'+innerHeight+'px')
    }
    document.querySelector('.jscad-container').setAttribute('style','width:'+innerWidth/1.7+'px')
}



/**
 * Animation loop. Runs with every frame to draw the program on the display.
 */ 
function animate() {
    requestAnimationFrame(animate)
    GlobalVariables.c.clearRect(0, 0, GlobalVariables.canvas.width, GlobalVariables.canvas.height)
    GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(atom => {
        atom.update()
    })
}

init()

//This module is used to create atoms which do not have a set number of inputs, but instead always have one input free.

/**
 * Computes the number of inputs which are curently available on a target atom.
 * @param {object} target - The atom which should have it's number of inputs computed.
 */ 
const howManyInputPortsAvailable = function(target){
    var portsAvailable = 0
    target.inputs.forEach(io => {
        if(io.type == 'input' && io.connectors.length == 0){   //if this port is available
            portsAvailable = portsAvailable + 1  //Add one to the count
        }
    })
    return portsAvailable
}

/**
 * Deletes one free input from an atom which has more than one free input available.
 * @param {object} target - The atom which should have one input deleted (if there are more than two free).
 */ 
const deleteEmptyPort = function(target){
    var i
    for (i = 0; i < target.inputs.length - 1; i++) {
        var io = target.inputs[i]
        if(io.type == 'input' && io.connectors.length == 0){
            target.removeIO('input', io.name, target)
            return
        }
    }
}

/**
 * Finds the highest number input currently used by this atom
 * @param {object} target - The atom which should be inspected for inputs.
 */ 
const findHighestInput = function(target){
    var maxInput = 0
    target.inputs.forEach( input => {
        maxInput = Math.max(maxInput, parseInt(input.name.match(/\d+$/)[0]))
    })
    return maxInput
}

/**
 * Adds or deletes inputs from a target atom until there is exactly one input available.
 * @param {object} target - The atom which should have it's number of inputs adjusted.
 */ 
export const addOrDeletePorts = (target) => {
    
    //Add or delete ports as needed
    if(howManyInputPortsAvailable(target) == 0){ //We need to make a new port available
        findHighestInput(target)
        target.addIO('input', '3D shape ' + (findHighestInput(target) + 1), target, 'geometry', '', true)
    }
    if(howManyInputPortsAvailable(target) >= 2){  //We need to remove the empty port
        deleteEmptyPort(target)
    }
}

/**
 * This class defines a BOMEntry object which is used to define one entry in a bill of materials.
 */ 
export class BOMEntry {
    /**
     * The constructor returns a new blank BOMEntry object.
     */ 
    constructor(){
        /** 
         * The name of the item.
         * @type {string}
         */
        this.BOMitemName  = 'name'
        /** 
         * The number of this item needed.
         * @type {number}
         */
        this.numberNeeded = 1
        /** 
         * The cost of one of this item in USD.
         * @type {number}
         */
        this.costUSD      = 0
        /** 
         * A link to where to purchase the item.
         * @type {string}
         */
        this.source       = 'www.example.com'
        /** 
         * The total number of this item needed for this part. Should this be removed in favor of  forcing the parts to be modeled? Probably nobody would bother.
         * @type {number}
         */
        this.totalNeeded  = this.numberNeeded //Scaled by the number of this instance
        /** 
         * This atom's radius as displayed on the screen is 1/72 width
         * @type {number}
         */
        this.radius = 1/65
        /**
         * This atom's height as drawn on the screen
         */
        this.height
    }
}

/**
 * Computes and returns an array of BOMEntry objects after looking at the tags of a geometry.
 * @param {object} geometry - The geometry which should be scanned for tags.
 */ 
export const extractBomTags = function(path, functionToPlace){
    //Extract all of the tags
    window.ask({key: "listTags", readPath: path }).then( tags => {
        // Filter for only bomItems
        var bomItems = tags.filter(item => {
            return item.substring(2, 13) == "BOMitemName"
        })
        
        bomItems = bomItems.map(JSON.parse)
        
        // Consolidate similar items into a single item
        var compiledArray = []
        bomItems.forEach(function (bomElement) {
            if (!this[bomElement.BOMitemName]) {                    //If the list of items doesn't already have one of these
                this[bomElement.BOMitemName] = new BOMEntry             //Create one
                this[bomElement.BOMitemName].numberNeeded = 0           //Set the number needed to zerio initially
                this[bomElement.BOMitemName].BOMitemName = bomElement.BOMitemName   //With the information from the item
                this[bomElement.BOMitemName].source = bomElement.source
                compiledArray.push(this[bomElement.BOMitemName])
            }
            this[bomElement.BOMitemName].numberNeeded += bomElement.numberNeeded
            this[bomElement.BOMitemName].costUSD += bomElement.costUSD
        }, Object.create(null))
        
        // Alphabetize by source
        compiledArray = compiledArray.sort((a,b) => (a.source > b.source) ? 1 : ((b.source > a.source) ? -1 : 0)) 
        
        functionToPlace(compiledArray)
    })
}

import Molecule from './molecules/molecule.js'
import GlobalVariables from './globalvariables.js'
import { extractBomTags } from './BOM.js'
import { OAuth } from 'oauthio-web'

/**
 * This function works like a class to sandbox interaction with GitHub.
 */
export default function GitHubModule(){
    const Octokit = require('@octokit/rest')
    /** 
     * The octokit instance which allows authenticated interaction with GitHub.
     * @type {object}
     */
    var octokit = new Octokit()
    /** 
     * The HTML element which is the popup.
     * @type {object}
     */
    var popup = document.getElementById('projects-popup')
    /** 
     * The name of the current repo.
     * @type {string}
     */
    var currentRepoName = null
    /** 
     * The name of the currently logged in user.
     * @type {string}
     */
    var currentUser = null
    /** 
     * The text to display at the top of the bill of materials.
     * @type {string}
     */
    var bomHeader = "###### Note: Do not edit this file directly, it is automatically generated from the CAD model \n# Bill Of Materials \n |Part|Number Needed|Price|Source| \n |----|----------|-----|-----|"
     
    /** 
     * The text to display at the top of the ReadMe file.
     * @type {string}
     */
    var readmeHeader = "###### Note: Do not edit this file directly, it is automatically generated from the CAD model"

    /** 
     * The timer used to trigger saving of the file.
     * @type {object}
     */
    var intervalTimer

    /** 
     * The timer used to trigger saving of the file.
     * @type {object}
     */
    var page = 1

    //Github pop up event listeners
    document.getElementById("loginButton").addEventListener("mousedown", () => {
        this.tryLogin()
    })
    
    /** 
     * Try to login using the oauth popup.
     */
    this.tryLogin = function(){
        
        // Initialize with OAuth.io app public key
        if(window.location.href.includes('private')){
            OAuth.initialize('6CQQE8MMCBFjdWEjevnTBMCQpsw') //app public key for repo scope
        }
        else{
            OAuth.initialize('BYP9iFpD7aTV9SDhnalvhZ4fwD8') //app public key for public_repo scope
        }
        // Use popup for oauth
        OAuth.popup('github').then(github => {
        
            /** 
         * Oktokit object to access github
         * @type {object}
         */   
            octokit = new Octokit({
                auth: github.access_token
            })
            
            //Test the authentication 
            octokit.users.getAuthenticated({}).then(result => {
                currentUser = result.data.login
                this.showProjectsToLoad()
            })
        })
    }
    
    /** 
     * Display projects which can be loaded in the popup.
     */
    this.showProjectsToLoad = function(){
        //Remove everything in the popup now
        while (popup.firstChild) {
            popup.removeChild(popup.firstChild)
        }

        //Close button (Mac style)
        if(GlobalVariables.topLevelMolecule && GlobalVariables.topLevelMolecule.name != "Maslow Create"){ //Only offer a close button if there is a project to go back to
            var closeButton = document.createElement("button")
            closeButton.setAttribute("class", "closeButton")
            closeButton.addEventListener("click", () => {
                popup.classList.add('off')
            })
            popup.appendChild(closeButton)
        }
        //Welcome title
        var welcome = document.createElement("div")
        welcome.setAttribute("style", " display: flex; margin: 10px; align-items: center;")
        popup.appendChild(welcome)

        var welcome1 = document.createElement("IMG")
        welcome1.setAttribute("src", "/imgs/maslow-logo.png" )
        welcome1.setAttribute("style", " height:25px; border-radius:50%;")
        welcome.appendChild(welcome1)
        var welcome2 = document.createElement("IMG")
        welcome2.setAttribute("src", "/imgs/maslowcreate.svg" )
        welcome2.setAttribute("style", "height:20px; padding: 10px;")
        welcome.appendChild(welcome2)
        var middleBrowseDiv = document.createElement("div")
        if (currentUser == null){

            var githubSign = document.createElement("button")
            githubSign.setAttribute("id", "loginButton2" )
            githubSign.setAttribute("class", "form browseButton githubSign")
            githubSign.setAttribute("style", "width: 90px; font-size: .7rem; margin-left: auto;")
            githubSign.textContent = "Login"
            welcome.appendChild(githubSign)   

            var githubSignUp = document.createElement("button")
            githubSignUp.setAttribute("class", "form browseButton githubSign")
            githubSignUp.setAttribute("onclick", "window.open('https://github.com/join')")
            githubSignUp.setAttribute("style", "width: 130px; font-size: .7rem;margin-left: 5px;")
            githubSignUp.textContent = "Create an account"
            welcome.appendChild(githubSignUp)  

            //Welcome title
            var welcome3 = document.createElement("div")
            welcome3.innerHTML = "Maslow Create User Projects"
            welcome3.setAttribute("style", "justify-content: flex-start; display: inline; width: 100%; font-size: 18px;")
            popup.appendChild(welcome3)

            middleBrowseDiv.setAttribute("style","margin-top:25px")

            githubSign.addEventListener("mousedown", () => {
                this.tryLogin()
            })
        }
        
        popup.classList.remove('off')
        popup.setAttribute("style", "padding: 0;text-align: center; background-color: #f9f6f6; border: 10px solid #3e3d3d;")
       
        var tabButtons = document.createElement("DIV")
        tabButtons.setAttribute("class", "tab")
        popup.appendChild(tabButtons)
     
        middleBrowseDiv.setAttribute("class", "middleBrowse")
        popup.appendChild(middleBrowseDiv)

        var searchIcon = document.createElement("IMG")
        searchIcon.setAttribute("src", '/imgs/search_icon.svg')
        searchIcon.setAttribute("style", "width: 20px; float: right; color: white; position: relative;right: 3px; opacity: 0.5;")
        middleBrowseDiv.appendChild(searchIcon)

        var searchBar = document.createElement("input")
        searchBar.setAttribute("type", "text")
        searchBar.setAttribute("contenteditable", "true")
        searchBar.setAttribute("placeholder", "Search for project..")
        searchBar.setAttribute("class", "menu_search")
        searchBar.setAttribute("id", "project_search")
        middleBrowseDiv.appendChild(searchBar)

        //Display option buttons
        var browseDisplay1 = document.createElement("div")
        browseDisplay1.setAttribute("class", "browseDisplay")
        var listPicture = document.createElement("IMG")
        listPicture.setAttribute("src", '/imgs/list-with-dots.svg') //https://www.freeiconspng.com/img/1454
        listPicture.setAttribute("style", "height: 75%;padding: 3px;")
        browseDisplay1.appendChild(listPicture)
        middleBrowseDiv.appendChild(browseDisplay1)
        var browseDisplay2 = document.createElement("div")
        browseDisplay2.setAttribute("class", "browseDisplay active_filter")
        browseDisplay2.setAttribute("id", "thumb")
        var listPicture2 = document.createElement("IMG")
        listPicture2.setAttribute("src", '/imgs/thumb_icon.png') 
        listPicture2.setAttribute("style", "height: 80%;padding: 3px;")
        browseDisplay2.appendChild(listPicture2)
        middleBrowseDiv.appendChild(browseDisplay2)

        //Input to search for projects

        searchBar.addEventListener('keydown', (e) => {
            
            this.loadProjectsBySearch("yoursButton",e, searchBar.value, "updated")
            //this.loadProjectsBySearch("githubButton",e, searchBar.value, "stars") // updated just sorts content by most recently updated
        })
        

        this.projectsSpaceDiv = document.createElement("DIV")
        this.projectsSpaceDiv.setAttribute("class", "float-left-div")
        this.projectsSpaceDiv.setAttribute("style", "overflow-x: hidden; margin-top: 10px;")
        popup.appendChild(this.projectsSpaceDiv)
        
        const pageChange = document.createElement("div")
        const pageBack = document.createElement("button")
        pageBack.setAttribute("id", "back")
        pageBack.setAttribute("class", "page_change")
        pageBack.innerHTML = "&#8249;"

        const pageForward = document.createElement("button")
        pageChange.appendChild(pageBack)
        pageChange.appendChild(pageForward)
        pageForward.setAttribute("id", "forward")
        pageForward.setAttribute("class", "page_change")
        pageForward.innerHTML = "&#8250;"

        popup.appendChild(pageChange)

        
        this.openTab(page)

        //Event listeners 

        browseDisplay1.addEventListener("click", () => {
            // titlesDiv.style.display = "flex"
            browseDisplay2.classList.remove("active_filter")
            this.openTab(page)
        })
        browseDisplay2.addEventListener("click", () => {
            // titlesDiv.style.display = "none"
            browseDisplay2.classList.add("active_filter")
            this.openTab(page)
        })
        pageForward.addEventListener("click", () => {
            if (page >=1){ page +=1 }
            this.openTab(page)
        })
        pageBack.addEventListener("click", () => {
            if (page >1){page -=1}
            this.openTab(page)
        })

    }

    /** 
     * Search for the name of a project and then return results which match that search.
     */
    this.loadProjectsBySearch = function(tabName, ev, searchString, sorting, pageNumber){
        if(ev.key == "Enter"){
            //Remove projects shown now
            while (this.projectsSpaceDiv.firstChild) {
                this.projectsSpaceDiv.removeChild(this.projectsSpaceDiv.firstChild)
            }
            // add initial projects to div

            //New project div
            if (currentUser !== null){
                var browseDiv = document.createElement("div")
                browseDiv.setAttribute("class", "browseDiv")
                this.projectsSpaceDiv.appendChild(browseDiv)
            
                var createNewProject = document.createElement("div")
                createNewProject.setAttribute("class", "newProject")

                browseDiv.appendChild(createNewProject)
                this.NewProject("New Project", null, true, "")
            }
            //header for project list style display
            var titlesDiv = document.createElement("div")
            titlesDiv.setAttribute("id","titlesDiv")
            var titles = document.createElement("div")
            titles.innerHTML = ""
            titles.setAttribute("class","browseColumn")
            titlesDiv.appendChild(titles)
            var titles2 = document.createElement("div")
            titles2.innerHTML = "Project"
            titles2.setAttribute("class","browseColumn")
            titlesDiv.appendChild(titles2)
            var titles3 = document.createElement("div")
            titles3.innerHTML = "Creator"
            titles3.setAttribute("class","browseColumn")
            titlesDiv.appendChild(titles3)
            var titles4 = document.createElement("div")
            titles4.innerHTML = "Created on"
            titles4.setAttribute("class","browseColumn")
            titlesDiv.appendChild(titles4)
            var titles5 = document.createElement("div")
            titles5.innerHTML = "Last Modified"
            titles5.setAttribute("class","browseColumn")
            titlesDiv.appendChild(titles5)

            if (!document.getElementById("thumb").classList.contains("active_filter")){
                titlesDiv.style.display = "flex"
                titlesDiv.style.marginTop = "10px"
                browseDiv.style.width = "100%"
                createNewProject.style.height = "80px"
            }
        
            this.projectsSpaceDiv.appendChild(titlesDiv)
              
            //Load projects
            var query
            var owned

            var sortMethod = sorting  //drop down input. temporarily inactive until we figure some better way to sort
            if(tabName == "yoursButton"){
                owned = true
                query = searchString + ' ' + 'fork:true user:' + currentUser + ' topic:maslowcreate'
            }
            else{
                owned = false
                query = searchString + ' topic:maslowcreate -user:' + currentUser
            }
            
            //Figure out how many repos this user has, search will throw an error if they have 0;
            octokit.repos.list({
                affiliation: 'owner',
            })
            
            octokit.search.repos({
                q: query,
                sort: sortMethod,
                per_page: 50,
                page: pageNumber,
                headers: {
                    accept: 'application/vnd.github.mercy-preview+json'
                }
            }).then(result => {
                result.data.items.forEach(repo => {
                    const thumbnailPath = "https://raw.githubusercontent.com/"+repo.full_name+"/master/project.svg?sanitize=true"
                    
                    this.addProject(repo.name, repo.id, repo.owner.login, repo.created_at, repo.updated_at, owned, thumbnailPath)
                })
                
            }) 
        } 
    }
    
    /** 
     * Adds a new project to the load projects display.
     */
    this.NewProject = function(projectName, id, owned, thumbnailPath){
        //create a project element to display
        
        var project = document.createElement("DIV")
        project.classList.add("newProjectdiv")
        
        var projectPicture = document.createElement("IMG")
        projectPicture.setAttribute("src", thumbnailPath)
        projectPicture.setAttribute("onerror", "this.src='/defaultThumbnail.svg'")
        projectPicture.setAttribute("style", "height: 80%; float: left;")
        project.appendChild(projectPicture)
        
        var projectText = document.createElement("span")
        projectText.innerHTML = "Start a new project"
        projectText.setAttribute("style","align-self: center")
        project.appendChild(projectText)

        document.querySelector(".newProject").appendChild(project) 
        
        project.addEventListener('click', () => {
            this.projectClicked(projectName, id, owned)
        })

    }
    
    /** 
     * Adds a new project to the load projects display.
     */
    this.addProject = function(projectName, id, owner, createdAt, updatedAt, owned, thumbnailPath){
        
        this.projectsSpaceDiv.classList.remove("float-left-div-thumb")
        var project = document.createElement("DIV")
        var projectPicture = document.createElement("IMG")
        projectPicture.setAttribute("src", thumbnailPath)
        projectPicture.setAttribute("onerror", "this.src='/defaultThumbnail.svg'")
        project.appendChild(projectPicture)
        project.setAttribute("id", projectName)
        project.classList.add("project")

        if (owned){
            project.classList.add("mine")
        }

        //create a project element to display
        if (document.getElementById("thumb").classList.contains("active_filter")){
            
            projectPicture.setAttribute("style", "width: 100%; height: 80%;")
            project.appendChild(document.createElement("BR"))

            var shortProjectName
            if(projectName.length > 13){
                shortProjectName = document.createTextNode(projectName.substr(0,9)+"..")
            }
            else{
                shortProjectName = document.createTextNode(projectName)
            }
            project.setAttribute("title",projectName)
            project.appendChild(shortProjectName) 
        }
        else{
            project.setAttribute("style", "display:flex; flex-direction:row; flex-wrap:wrap; width: 100%; border-bottom: 1px solid darkgrey;")
            projectPicture.setAttribute("class", "browseColumn")
            
            shortProjectName = document.createElement("DIV")
            shortProjectName.innerHTML = projectName
            shortProjectName.setAttribute("class", "browseColumn")
            project.appendChild(shortProjectName) 

            var ownerName = document.createElement("DIV")
            var ownerNameIn = document.createTextNode(owner)
            ownerName.appendChild(ownerNameIn) 
            ownerName.setAttribute("class", "browseColumn")
            project.appendChild(ownerName) 

            var date = new Date(createdAt)
            var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
            var createdTime = document.createElement("DIV")
            createdTime.setAttribute("class", "browseColumn")
            var createdTimeIn = document.createTextNode(months[date.getMonth()] + " " + date.getFullYear())
            createdTime.appendChild(createdTimeIn) 
            project.appendChild(createdTime) 

            var updated = new Date(updatedAt)
            var updatedTime = document.createElement("DIV")
            var updatedTimeIn = document.createTextNode(months[updated.getMonth()] + " " + date.getFullYear())
            updatedTime.appendChild(updatedTimeIn)
            updatedTime.setAttribute("class", "browseColumn")
            project.appendChild(updatedTime) 
   
        }

        this.projectsSpaceDiv.appendChild(project) 

        document.getElementById(projectName).addEventListener('click', () => {
            this.projectClicked(projectName, id, owned)
        })
    }
    
    /** 
     * Runs when you click on a project.
     */
    this.projectClicked = function(projectName, projectID, owned){
        //runs when you click on one of the projects
        if(projectName == "New Project"){
            this.createNewProjectPopup()
        }
        else if(owned){
            this.loadProject(projectName)
        }
        else{
            window.open('/run?'+projectID)
        }
    }
    
    /** 
     * Runs owned search first and then full github search
     */
    this.openTab = function(page) {

        // Show the current tab, and add an "active" class to the button that opened the tab
        //Click on the search bar so that when you start typing it shows updateCommands
        document.getElementById('menuInput').focus()
        
        this.loadProjectsBySearch("yoursButton", {key: "Enter"}, document.getElementById("project_search").value, "updated", page)
        //this.loadProjectsBySearch("githubButton", {key: "Enter"}, document.getElementById("project_search").value, "stars", page)
    }
    
    /** 
     * The popup to create a new project (giving it a name and whatnot).
     */
    this.createNewProjectPopup = function(){
        //Clear the popup and populate the fields we will need to create the new repo
        
        while (popup.firstChild) {
            popup.removeChild(popup.firstChild)
        }
        popup.setAttribute("style", "padding:2% 0")
        //Project name
        // <div class="form">
        var createNewProjectDiv = document.createElement("DIV")
        createNewProjectDiv.setAttribute("class", "form")
        createNewProjectDiv.setAttribute("style", "color:whitesmoke")
        
        //Add a title
        var header = document.createElement("H1")
        var title = document.createTextNode("Create a new project")
        header.appendChild(title)
        createNewProjectDiv.appendChild(header)
        
        //Create the form object
        var form = document.createElement("form")
        form.setAttribute("class", "login-form")
        createNewProjectDiv.appendChild(form)
        
        //Create the name field
        var name = document.createElement("input")
        name.setAttribute("id","project-name")
        name.setAttribute("type","text")
        name.setAttribute("placeholder","Project name")
        form.appendChild(name)
        
        //Add the description field
        var description = document.createElement("input")
        description.setAttribute("id", "project-description")
        description.setAttribute("type", "text")
        description.setAttribute("placeholder", "Project description")
        form.appendChild(description)
        
        //Add the button
        var createButton = document.createElement("button")
        createButton.setAttribute("type", "button")
        createButton.setAttribute("style", "height: 50px; border: 1px solid whitesmoke;")
        createButton.addEventListener('click', () => {
            this.createNewProject()
        })
        var buttonText = document.createTextNode("Create Project")
        createButton.appendChild(buttonText)
        form.appendChild(createButton)
    
        popup.appendChild(createNewProjectDiv)

    }
    
