import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables.js'

/**
 * This class creates the circle atom.
 */
export default class Circle extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Circle'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Circle'
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Creates a new circle."
        
        this.addIO('input', 'diameter', this, 'number', 10)
        this.addIO('output', 'geometry', this, 'geometry', '')
        
        this.setValues(values)
    }

    /**
     * Starts propagation from this atom if it is not waiting for anything up stream.
     */ 
    beginPropagation(force = false){
        //Check to see if a value already exists. Generate it if it doesn't. Only do this for circles and rectangles
        if(!GlobalVariables.availablePaths.includes(this.path)||force){
            //Triggers inputs with nothing connected to begin propagation
            this.inputs.forEach(input => {
                input.beginPropagation()
            })
        }
    }

    /**
     * Draw the circle atom & icon.
     */ 
    draw(){

        super.draw() //Super call to draw the rest

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.arc(GlobalVariables.widthToPixels(this.x), 
            GlobalVariables.heightToPixels(this.y), 
            GlobalVariables.widthToPixels(this.radius/2), 0, Math.PI * 2, false)
        //GlobalVariables.c.fill()
        GlobalVariables.c.stroke() 
        GlobalVariables.c.closePath() 
    }
    
    /**
     * Super class the default update value function. This function computes the number of points to use for the circle and then calls the worker thread to create the circle.
     */ 
    updateValue(){
        try{
            var diameter = this.findIOValue('diameter')
            
            const numberOfSegments = Math.min(Math.max(parseInt(Math.PI / (Math.acos(1 - GlobalVariables.circleSegmentSize/(diameter/2)))),6), 100)
            
            const values = {key: "circle", diameter: diameter, numSegments:numberOfSegments, writePath: this.path }
            this.basicThreadValueProcessing(values)
        }catch(err){this.setAlert(err)}
    }
}

import Atom from '../prototypes/atom.js'
import CodeMirror from 'codemirror'
import GlobalVariables from '../globalvariables'

/**
 * The Code molecule type adds support for executing arbirary jsxcad code.
 */
export default class Code extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = "Code"
        /**
         * This atom's name
         * @type {string}
         */
        this.atomType = "Code"
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Defines a JSxCAD code block."
        /**
         * The code contained within the atom stored as a string.
         * @type {string}
         */
        this.code = "//You can learn more about all of the available methods at https://jsxcad.js.org/app/UserGuide.html \n\n\nfunction main(Input1, Input2){\n  return Sphere(40)        //return must be geometry;\n}\n\nreturn main(Input1, Input2)"
        
        this.addIO("output", "geometry", this, "geometry", "")
        
        this.setValues(values)
        
        this.parseInputs(false)
    }

    /**
     * Draw the code atom which has a code icon.
     */ 
    draw(){

        super.draw() //Super call to draw the rest
        
        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.font = `${GlobalVariables.widthToPixels(this.radius)}px Work Sans Bold`
        GlobalVariables.c.fillText('</>', GlobalVariables.widthToPixels(this.x - (this.radius/1.5)), GlobalVariables.heightToPixels(this.y + (this.radius*1.5)))
    }
    
    /**
     * Grab the code as a text string and execute it. 
     */ 
    updateValue(){
        try{
            this.parseInputs()
            
            var argumentsArray = {}
            this.inputs.forEach(input => {
                argumentsArray[input.name] = input.value
            })
            
            const values = { key: "code", code: this.code, paths: argumentsArray, writePath: this.path }
            this.basicThreadValueProcessing(values)
            
        }catch(err){this.setAlert(err)}
    }
    
    /**
     * This function reads the string of inputs the user specifies and adds them to the atom.
     */ 
    parseInputs(ready = true){
        //Parse this.code for the line "\nmain(input1, input2....) and add those as inputs if needed
        var variables = /\(\s*([^)]+?)\s*\)/.exec(this.code)
        if (variables[1]) {
            variables = variables[1].split(/\s*,\s*/)
        }
        
        //Add any inputs which are needed
        for (var variable in variables){
            if(!this.inputs.some(input => input.Name === variables[variable])){
                this.addIO('input', variables[variable], this, 'geometry', null, ready)
            }
        }
        
        //Remove any inputs which are not needed
        for (var input in this.inputs){
            if( !variables.includes(this.inputs[input].name) ){
                this.removeIO('input', this.inputs[input].name, this)
            }
        }
    }
    
    /**
     * Edit the atom's code when it is double clicked
     * @param {number} x - The X cordinate of the click
     * @param {number} y - The Y cordinate of the click
     */ 
    doubleClick(x,y){
        //returns true if something was done with the click
        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        var clickProcessed = false
        
        var distFromClick = GlobalVariables.distBetweenPoints(x, xInPixels, y, yInPixels)
        
        if (distFromClick < xInPixels){
            this.editCode()
            clickProcessed = true
        }
        
        return clickProcessed 
    }
    
    /**
     * Callled to trigger editing the code atom
     */ 
    editCode(){
        //Remove everything in the popup now
        const popup = document.getElementById('projects-popup')
        while (popup.firstChild) {
            popup.removeChild(popup.firstChild)
        }
        
        popup.classList.remove('off')

        //Add a title
        var codeMirror = CodeMirror(popup, {
            value: this.code,
            mode:  "javascript",
            lineNumbers: true,
            gutter: true,
            lineWrapping: true
        })
        
        var form = document.createElement("form")
        popup.appendChild(form)
        var button = document.createElement("button")
        button.setAttribute("type", "button")
        button.appendChild(document.createTextNode("Save Code"))
        button.addEventListener("click", () => {
            this.code = codeMirror.getDoc().getValue('\n')
            this.updateValue()
            popup.classList.add('off')
        })
        form.appendChild(button)
    }
    
    /**
     * Add a button to open the code editor to the side bar
     */ 
    updateSidebar(){
        var valueList =  super.updateSidebar() 
        
        this.createButton(valueList,this,"Edit Code",() => {
            this.editCode()
        })
    }
    
    /**
     * Save the input code to be loaded next time
     */ 
    serialize(values){
        //Save the readme text to the serial stream
        var valuesObj = super.serialize(values)
        
        valuesObj.code = this.code
        
        return valuesObj
        
    }
}

