import Atom from '../prototypes/atom.js'
import GlobalVariables from '../globalvariables.js'
import saveAs from '../lib/FileSaver.js'

/**
 * This class creates the circle atom.
 */
export default class Gcode extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Gcode'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Gcode'
        /**
         * This atom's height as drawn on the screen
         */
         
        this.height = 16
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Generates Maslow gcode from the input geometry."
        /**
         * The generated gcode string
         * @type {string}
         */
        this.gcodeString = ""
        
        this.addIO('input', 'geometry', this, 'geometry', null)
        this.addIO('input', 'tool size', this, 'number', 6.35)
        this.addIO('input', 'passes', this, 'number', 6)
        this.addIO('input', 'speed', this, 'number', 500)
        this.addIO('input', 'tabs', this, 'string', "true")
        this.addIO('input', 'safe height', this, 'number', 6)
        
        this.addIO('output', 'gcode', this, 'geometry', '')
        
        this.setValues(values)
    }
    
    /**
     * Draw the circle atom & icon.
     */ 
    draw(){

        super.draw() //Super call to draw the rest

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#484848'
        GlobalVariables.c.font = `${GlobalVariables.widthToPixels(this.radius)}px Work Sans Bold`
        GlobalVariables.c.fillText('G', GlobalVariables.widthToPixels(this.x- this.radius/3), GlobalVariables.heightToPixels(this.y)+this.height/3)
        GlobalVariables.c.fill()
        GlobalVariables.c.closePath()

    }

    /**
     * Generate a layered outline of the part where the tool will cut
     */ 
    updateValue(){
        try{
            var geometry = this.findIOValue('geometry')
            var toolSize = this.findIOValue('tool size')
            var passes = this.findIOValue('passes')
            var speed = this.findIOValue('speed')
            var tabs = this.findIOValue('tabs')
            var safeHeight = this.findIOValue('safe height')
            const values = {key: "gcode", readPath:geometry, toolSize:toolSize, passes:passes, speed:speed, tabs:tabs, safeHeight:safeHeight ,writePath: this.path }
            this.gcodeString = this.basicThreadValueProcessing(values)
        }catch(err){this.setAlert(err)}
    }
    
    /**
     * Create a button to download the .stl file.
     */ 
    updateSidebar(){
        const list = super.updateSidebar()
        this.createButton(list, this, "Download G-Code", ()=>{this.downloadGCode()})
    }
    
    /**
     * The function which is called when you press the download button.
     */ 
    downloadGCode(){
        try{
            var geometry = this.findIOValue('geometry')
            var toolSize = this.findIOValue('tool size')
            var passes = this.findIOValue('passes')
            var speed = this.findIOValue('speed')
            var tabs = this.findIOValue('tabs')
            var safeHeight = this.findIOValue('safe height')
            const values = {key: "gcode", readPath:geometry, toolSize:toolSize, passes:passes, speed:speed, tabs:tabs, safeHeight:safeHeight ,writePath: this.path }
            window.ask(values).then( returnedAnswer => {
                const blob = new Blob([returnedAnswer])
                saveAs(blob, GlobalVariables.currentMolecule.name+'.nc')
            })
        }catch(err){this.setAlert(err)}
        
    }
    
}

import Atom from '../prototypes/atom.js'
import GlobalVariables from '../globalvariables'

/**
 * This class creates the Genetic Algorithm atom.
 */
export default class GeneticAlgorithm extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Genetic Algorithm'
        
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Genetic Algorithm'
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Defines a new genetic algorithm which will simulate evolution to maximize the fitness function using the input population size and number of generations. Only constants which are upstream of this atom with evolve checked will be changed."
        
        /**
         * An array of constant objects which need to be evolved
         * @type {array}
         */
        this.constantsToEvolve = []
        
        /**
         * An array of objects representing the current population
         * @type {array}
         */
        this.population = []
        
        /**
         * Current individual being evaluated in this generation
         * @type {integer}
         */
        this.individualIndex = 0
         
        /**
         * Current generation
         * @type {integer}
         */
        this.generation = 0
        
        /**
         * Top fitness value for the current generation
         * @type {float}
         */
        this.topFitness = 0
        
        /**
         * A flag to indicate if evolution is in process
         * @type {boolean}
         */
        this.evolutionInProcess = false
        
        this.addIO('input', 'fitness function', this, 'number', 0)
        this.addIO('input', 'population size', this, 'number', 50)
        this.addIO('input', 'number of generations', this, 'number', 10)
        
        this.setValues(values)
        
        this.updateValue()
    }

    /**
     * Draw the code atom which has a code icon.
     */ 
    draw(){

        super.draw() //Super call to draw the rest
         

        const xInPixels = GlobalVariables.widthToPixels(this.x)
        const yInPixels = GlobalVariables.heightToPixels(this.y)
        const radiusInPixels = GlobalVariables.widthToPixels(this.radius)
      
       
        GlobalVariables.c.beginPath()
        GlobalVariables.c.arc(GlobalVariables.widthToPixels(this.x - this.radius/5), 
            GlobalVariables.heightToPixels(this.y), 
            GlobalVariables.widthToPixels(this.radius/2), Math.PI *3.4, Math.PI * 2.7, false) 
        GlobalVariables.c.stroke() 
        GlobalVariables.c.closePath()

        GlobalVariables.c.beginPath()
        GlobalVariables.c.arc(GlobalVariables.widthToPixels(this.x + this.radius/5), 
            GlobalVariables.heightToPixels(this.y ), 
            GlobalVariables.widthToPixels(this.radius/2), Math.PI *3.6, Math.PI * 2.3, true) 
        GlobalVariables.c.stroke() 
        GlobalVariables.c.closePath()  

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/3, yInPixels )
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/3, yInPixels )
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/3, yInPixels - radiusInPixels/5 )
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/3, yInPixels - radiusInPixels/5)
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/3, yInPixels + radiusInPixels/5 )
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/3, yInPixels + radiusInPixels/5)
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()



    }
    
    /**
     * Generate a layered outline of the part where the tool will cut
     */ 
    updateValue(){
        this.decreaseToProcessCountByOne()
        if(this.evolutionInProcess){
            this.updateSidebar()
            /**
             * Atom is processing
            * @type {boolean}
             */
            this.processing = true
            //Store the result from this individual in it's fitness value
            this.population[this.individualIndex].fitness = this.findIOValue('fitness function')
            
            //Evaluate the next individual
            this.individualIndex ++
            if(this.individualIndex < this.findIOValue('population size')){
                //Evaluate the next individual by updating all of the inputs
                this.beginEvaluatingIndividual()
            }
            else{
                this.generation++
                if(this.generation < this.findIOValue('number of generations')){
                    // Generate a new generation from the existing generation and start the process over
                    this.breedAndCullPopulation()
                    this.individualIndex = 0
                    this.beginEvaluatingIndividual()
                }
                else{
                    this.evolutionInProcess = false
                    this.processing = false
                    // Set the inputs to the prime candidate
                    this.population = this.population.sort((a, b) => parseFloat(b.fitness) - parseFloat(a.fitness))
                    this.individualIndex = 0
                    this.generation = 0
                    this.beginEvaluatingIndividual()
                    
                    this.updateSidebar()
                }
            }
        }
    }
    
    /**
     * Add a button to trigger the evolution process
     */ 
    updateSidebar(){
        
        
        
        if(this.evolutionInProcess){
            
            //Remove everything in the sidebar
            let sideBar = document.querySelector('.sideBar')
            while (sideBar.firstChild) {
                sideBar.removeChild(sideBar.firstChild)
            }
            
            //Generate the list
            var sbList = document.createElement('ul')
            sideBar.appendChild(sbList)
            sbList.setAttribute('class', 'sidebar-list')
            
            //Add text to the list element
            var listElement = document.createElement('LI')
            sbList.appendChild(listElement)
            
            var labelDiv = document.createElement('div')
            listElement.appendChild(labelDiv)
            var labelText = document.createTextNode("Evolving...Generation: " + this.generation + "    Best Fitness Value: " + this.topFitness)
            labelDiv.appendChild(labelText)
            labelDiv.setAttribute('class', 'sidebar-subitem label-item')
        }
        else{
            
            var valueList =  super.updateSidebar() 
            
            this.createButton(valueList,this,'Evolve',() => {
                this.evolutionInProcess = true
                
                //Generate a list of all the constants to evolve
                this.updateConstantsList()
                
                //Generate a population from those constants
                this.initializePopulation()
                
                //Evaluate the first individual
                this.beginEvaluatingIndividual()
            })
        }
    }
    
    /**
     * Trigger the process to evaluate the current individual
     */ 
    beginEvaluatingIndividual(){
        const individualToEvaluate = this.population[this.individualIndex]
        
        //Lock all the constants
        individualToEvaluate.genome.forEach(gene => {
            gene.constantAtom.output.waitOnComingInformation()
        })
        
        //Set all of their values
        individualToEvaluate.genome.forEach(gene => {
            gene.constantAtom.output.value = gene.newValue
        })
        
        //Trigger them to update
        individualToEvaluate.genome.forEach(gene => {
            gene.constantAtom.updateValue()
        })
    }
    
    /**
     * Generate a random number between min and max
     */ 
    getRandomValue(min, max) {
        const randomVal = Math.random() * (max - min) + min
        return randomVal
    }
    
    /**
     * Generate an initial population
     */ 
    initializePopulation(){
        this.population = []
        this.individualIndex = 0
        this.generation = 0
        this.topFitness = 0
        
        var i = 0
        while(i < this.findIOValue('population size')){
            var genome = []
            this.constantsToEvolve.forEach(constant => {
                var gene = {
                    newValue: this.getRandomValue(constant.min, constant.max),
                    constantAtom: constant
                }
                genome.push(gene)
            })
            
            //Generate an individual with a random value for each input
            var individual = {
                genome: genome,
                fitness: null
            }
            this.population.push(individual)
            
            i++
        }
    }
    
    /**
     * Take two individuals and breed them to form a new individual with a mix of their genes and mutations
     */ 
    breedTwo(A, B){
        const lengthOfGenome = A.genome.length
        
        var child = {
            fitness: null,
            genome: []
        }
        
        var geneIndex = 0
        while(geneIndex < lengthOfGenome){
            var newGene = {
                newValue: null,
                constantAtom: A.genome[geneIndex].constantAtom
            }
            
            
            const maxVal         = A.genome[geneIndex].constantAtom.max
            const minVal         = A.genome[geneIndex].constantAtom.min
            const mutationAmount = 0.1*(maxVal-minVal)*(Math.random()-.5) //Mutate by at most +-.5%
            
            var newGeneVal = null
            if(Math.random() > 0.5){
                newGeneVal = A.genome[geneIndex].newValue+mutationAmount
            }else{
                newGeneVal = B.genome[geneIndex].newValue+mutationAmount
            }
            
            //Constrain to within bounds
            newGene.newValue = Math.min(Math.max(newGeneVal, minVal), maxVal)
            
            child.genome.push(newGene)
            
            geneIndex++
        }
        return child
    }
    
    /**
     * Breed the best performers in the population, cull the rest
     */ 
    breedAndCullPopulation(){
        this.population = this.population.sort((a, b) => parseFloat(b.fitness) - parseFloat(a.fitness))
        
        this.topFitness = this.population[0].fitness
        
        // Create a new population by taking the top 1/5th of the original population
        const keptPopulationNumber = Math.round(this.population.length / 5)
        
        const breeders = this.population.slice(0,keptPopulationNumber)
        
        //Generate a new population of individuals by breading from the last generation
        var newGeneration = []
        var index = 0
        while(index < this.population.length - keptPopulationNumber){
            const individualOneIndex = Math.round(Math.random()*(breeders.length-1))
            const individualTwoIndex = Math.round(Math.random()*(breeders.length-1))
            const newIndividual = this.breedTwo(breeders[individualOneIndex], breeders[individualTwoIndex])
            newGeneration.push(newIndividual)
            index = index + 1
        }
        
        this.population = breeders.concat(newGeneration)
    }
    
    /**
     * Regenerate the list of constants we are evolving
     */ 
    updateConstantsList(){
        //Create an array of the inputs by walking up stream
        this.constantsToEvolve = []
        this.inputs.forEach(input => {
            input.connectors.forEach(connector => {
                connector.walkBackForConstants(constantObject => {this.addToConstantsList(constantObject)})
            })
        })
    }
    
    /**
     * Add a constant to the list. Used as a callback from passing up the tree.
     */ 
    addToConstantsList(constantObject){
        this.constantsToEvolve.push(constantObject)
    }
    
}

import Molecule from '../molecules/molecule'
import GlobalVariables from '../globalvariables'

/**
 * This class creates the GitHubMolecule atom.
 */
export default class GitHubMolecule extends Molecule {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        super(values)

        /** 
         * This atom's name
         * @type {string}
         */
        this.name = 'Github Molecule'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'GitHubMolecule'
        /**
         * A flag to signal if this node is the top level node
         * @type {boolean}
         */
        this.topLevel = false
        /**
         * The color for the whole in the center of the drawing...probably doesn't need to be in this scope
         * @type {string}
         */
        this.centerColor = 'black'
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Project imported from GitHub"
        
        this.setValues(values)
    }
    
    /**
     * This replaces the default Molecule double click behavior to prevent you from being able to double click into a github molecule
     * @param {number} x - The x coordinate of the click
     * @param {number} y - The y coordinate of the click
     // */ 
    // doubleClick(x,y){
    // var clickProcessed = false
    // var distFromClick = GlobalVariables.distBetweenPoints(x, this.x, y, this.y)
    // if (distFromClick < this.radius){
    // clickProcessed = true
    // }
    // return clickProcessed 
    // }
    
    /**
     * Loads a project into this GitHub molecule from github based on the passed github ID. This function is async and execution time depends on project complexity, and network speed.
     * @param {number} id - The GitHub project ID for the project to be loaded.
     */ 
    async loadProjectByID(id){
        
        //Get the repo by ID
        const json = await GlobalVariables.gitHub.getProjectByID(id, this.topLevel)
        
        const projectData = await GlobalVariables.gitHub.getProjectDataByID(id)
        if(projectData){
            const values = {key: "fromJSON", writePath: this.path, json: projectData}
            window.ask(values)
        }
        
        //Store values that we want to overwrite in the loaded version
        var valuesToOverwriteInLoadedVersion
        if(this.topLevel){
            valuesToOverwriteInLoadedVersion = {atomType: this.atomType, topLevel: this.topLevel}
        }
        else{
            //If there are stored io values to recover
            if(this.ioValues != undefined){
                valuesToOverwriteInLoadedVersion = {uniqueID: this.uniqueID, x: this.x, y: this.y, atomType: this.atomType, topLevel: this.topLevel, ioValues: this.ioValues}
            }
            else{
                valuesToOverwriteInLoadedVersion = {uniqueID: this.uniqueID, x: this.x, y: this.y, atomType: this.atomType, topLevel: this.topLevel}
            }
        }
        const promsie =  this.deserialize(json, valuesToOverwriteInLoadedVersion, true).then( () => {
            if(!projectData){  //If we haven't loaded the project directly then recompute the ouput
                this.setValues(valuesToOverwriteInLoadedVersion)
                this.loadTree()
            }
        })
        return promsie
    }
    
    /**
     * Reload this github molecule from github
     */
    reloadMolecule(){
        
        //Delete everything currently inside...Make a copy to prevent index issues
        const copyOfNodesOnTheScreen = [...this.nodesOnTheScreen]
        copyOfNodesOnTheScreen.forEach(node => {
            node.deleteNode(false, false, true)
        })
        
        //Re-serialize this molecule
        this.loadProjectByID(this.projectID).then( ()=> {
            this.beginPropagation(true)
            this.updateSidebar()
        })
    }
    
    /**
     * Starts propagation from this atom if it is not waiting for anything up stream.
     */ 
    beginPropagation(force = false){
        
        //Tell every atom inside this molecule to begin Propagation
        super.beginPropagation(force)
        
        //Trigger the inputs to this github molecule if needed
        if(!GlobalVariables.availablePaths.includes(this.path) || force){
            //Triggers inputs with nothing connected to begin propagation
            this.inputs.forEach(input => {
                input.beginPropagation(force)
            })
        }
    }
    
    /**
     * Updates sidebar with buttons for user in runMode
     */
    updateSidebar(){
        const list = super.updateSidebar()
        
        if(this.topLevel){
            this.runModeSidebarAdditions.forEach(sideBarFunction => {
                sideBarFunction(list)
            })
            
            this.createButton(list, this, "Bill Of Materials", ()=>{
                GlobalVariables.gitHub.openBillOfMaterialsPage()
            })
            this.createButton(list, this, "Fork", ()=>{
                GlobalVariables.gitHub.forkByID(this.projectID)
            })
            this.createButton(list, this, "Star", ()=>{
                GlobalVariables.gitHub.starProject(this.projectID)
            })
        }
        else{
            this.createButton(list, this, "Reload", ()=>{this.reloadMolecule()})
        }
    }
    
    /**
     * Save the project information to be loaded. This should use super.serialize() to maintain a connection with Molecule, but it doesn't...should be fixed
     */ 
    serialize(){
        
        var ioValues = []
        this.inputs.forEach(io => {
            if (typeof io.getValue() == 'number'){
                var saveIO = {
                    name: io.name,
                    ioValue: io.getValue()
                }
                ioValues.push(saveIO)
            }
        })
        
        //Return a placeholder for this molecule
        var object = {
            atomType: this.atomType,
            name: this.name,
            x: this.x,
            y: this.y,
            uniqueID: this.uniqueID,
            projectID: this.projectID,
            ioValues: ioValues,
            simplify: this.simplify
        }
        
        return object
    }
    
}

import Atom from '../prototypes/atom.js'
import { addOrDeletePorts } from '../alwaysOneFreeInput.js'
import GlobalVariables from '../globalvariables.js'

/**
 * This class creates the group atom instance.
 */
export default class Group extends Atom{
    /**
    * Creates a new group atom.
    * @param {object} values - An object of values. Each of these values will be applied to the resulting atom.
    */
    constructor(values){
        super(values)
        
        this.addIO('output', 'geometry', this, 'geometry', '')
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Group'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Group'
        /**
         * A list of all of the inputs to this molecule. May be loaded when the molecule is created.
         * @type {array}
         */
        this.ioValues = []
        /**
         * A flag to determine if cutaway geometry is removed.
         * @type {boolean}
         */
        this.removeCutawayGeometry = true
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Groups shapes together into one. Shapes are allowed to overlap."
        
        this.setValues(values)
        
        //This loads any inputs which this atom had when last saved.
        if (typeof this.ioValues !== 'undefined'){
            this.ioValues.forEach(ioValue => { //for each saved value
                this.addIO('input', ioValue.name, this, 'geometry', '')
            })
        }
    }
    
    /**
     * Add or delete ports as needed in addition to the normal begin propogation stuff
     */ 
    beginPropagation(){
        
        addOrDeletePorts(this)  //Add or remove ports as needed
        
        super.beginPropagation()
    }
    
    /**
     * Draw the group icon
     */ 
    draw(){

        super.draw() //Super call to draw the rest

        const xInPixels = GlobalVariables.widthToPixels(this.x)
        const yInPixels = GlobalVariables.heightToPixels(this.y)
        const radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/2, yInPixels + radiusInPixels/2)
        //GlobalVariables.c.fill()
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()
        GlobalVariables.c.beginPath()
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/4, yInPixels - radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/4, yInPixels - radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/4, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/4, yInPixels - radiusInPixels/2)

        //GlobalVariables.c.fill()
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.lineJoin = "round"
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

    }

    /**
    * Super class the default update value function. This function computes creates an array of all of the input values and then passes that array to a worker thread to create the group.
    */ 
    updateValue(){
        if(this.inputs.every(x => x.ready)){
            try{
                var inputValues = []
                this.inputs.forEach( io => {
                    if(io.connectors.length > 0 && io.type == 'input'){
                        inputValues.push(io.getValue())
                    }
                })
                
                const values = { key: "group",paths: inputValues, writePath: this.path }
                
                this.basicThreadValueProcessing(values, "group")
                this.clearAlert()
            }catch(err){this.setAlert(err)}
            
            //Delete or add ports as needed
            addOrDeletePorts(this)
        }
    }
    
    /**
    * Super class the default serialize function to save the inputs since this atom has variable numbers of inputs.
    */ 
    serialize(savedObject){
        var thisAsObject = super.serialize(savedObject)
        
        var ioValues = []
        this.inputs.forEach(io => {
            if (io.connectors.length > 0){
                var saveIO = {
                    name: io.name,
                    ioValue: 10
                }
                ioValues.push(saveIO)
            }
        })
        
        thisAsObject.ioValues = ioValues
        
        return thisAsObject
    }
}

import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables'

/**
 * This class creates the input atom.
 */
export default class Input extends Atom {
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        super (values)
        
        /** 
         * This atom's name
         * @type {string}
         */
        this.name = 'Input' + GlobalVariables.generateUniqueID()
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Adds an input to the parent molecule. If the parent molecule is the top level of the project then the input will be available when the project is shared or imported into another project. Name is editable"
        /** 
         * The value the input is set to, defaults to 10. Is this still used or are we using the value of the attachmentPoint now?
         * @type {number}
         */
        this.value = 10
        /** 
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Input'
        /** 
         * This atom's height for drawing
         * @type {number}
         */
        this.height
        
        /** 
         * This atom's old name, used during name changes
         * @type {string}
         */
        this.oldName = this.name
        
        this.addIO('output', 'number or geometry', this, 'number or geometry', 10)
        
        //Add a new input to the current molecule
        if (typeof this.parent !== 'undefined') {
            this.parent.addIO('input', this.name, this.parent, 'number or geometry', 10)
        }
        
        this.setValues(values)
    }
    
    /**
     * Updates the side bar to let the user change the atom value. Note that the parent molecule input is set, not this atom's input by changes.
     */ 
    updateSidebar(){
        //updates the sidebar to display information about this node
        
        var valueList =  super.updateSidebar() //call the super function
        
        this.createEditableValueListItem(valueList,this,'name', 'Name', false)
        
    }
    
    /**
     * Draws the atom on the screen.
     */ 
    draw() {

        // //Snap the inputs to the far right side
        /**
        * The x position of the atom
        * @type {number}
        */
        this.x = this.radius

        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)
        /**
        * Relates height to radius
        * @type {number}
        */
        this.height = radiusInPixels*1.3
        //Check if the name has been updated
        if(this.name != this.oldName){this.updateParentName()}
        
        //Set colors
        if(this.processing){
            GlobalVariables.c.fillStyle = 'blue'
        }
        else if(this.selected){
            GlobalVariables.c.fillStyle = this.selectedColor
            GlobalVariables.c.strokeStyle = this.defaultColor
            /**
             * This background color
             * @type {string}
             */
            this.color = this.selectedColor
            /**
             * This atoms accent color
             * @type {string}
             */
            this.strokeColor = this.defaultColor
        }
        else{
            GlobalVariables.c.fillStyle = this.defaultColor
            GlobalVariables.c.strokeStyle = this.selectedColor
            this.color = this.defaultColor
            this.strokeColor = this.selectedColor
        }
        
        this.inputs.forEach(input => {
            input.draw()
        })
        if(this.output){
            this.output.draw()
        }
        if(this.showHover){
            GlobalVariables.c.textAlign = 'start' 
            GlobalVariables.c.fillText(this.name, xInPixels + radiusInPixels, yInPixels -radiusInPixels)
        }
        GlobalVariables.c.beginPath()
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels, yInPixels + this.height/2)
        GlobalVariables.c.lineTo(xInPixels, yInPixels + this.height/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels, yInPixels)
        GlobalVariables.c.lineTo(xInPixels, yInPixels - this.height/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels, yInPixels - this.height/2)
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.fill()
        GlobalVariables.c.closePath()
        GlobalVariables.c.stroke()
        
       
    }
    
    /**
     * Remove the input from the parent molecule, then delete the atom normally.
     */ 
    deleteNode(backgroundClickAfter = true, deletePath = true, silent = false) {
        //Remove this input from the parent molecule
        if (typeof this.parent !== 'undefined') {
            this.parent.removeIO('input', this.name, this.parent, silent)
        }
        
        super.deleteNode(backgroundClickAfter, deletePath, silent)
    }
    
    /**
     * Called when the name has changed to updated the name of the parent molecule IO
     */ 
    updateParentName(){
        //Run through the parent molecule and find the input with the same name
        this.parent.inputs.forEach(child => {
            if (child.name == this.oldName){
                child.name = this.name
            }
        })
        this.oldName = this.name
    }
    
    /**
     * Grabs the new value from the parent molecule's input, sets this atoms value, then propagates.
     */ 
    updateValue(){
        this.parent.inputs.forEach(input => { //Grab the value for this input from the parent's inputs list
            if(input.name == this.name){        //If we have found the matching input
                this.decreaseToProcessCountByOne()
                
                this.value = input.getValue()
                
                this.output.waitOnComingInformation()         //Lock all of the dependents
                
                this.output.setValue(this.value)
            }
        })
    }
    
    /**
     * Sets all the input and output values to match their associated atoms.
     */ 
    loadTree(){
        this.parent.inputs.forEach(input => { //Grab the value for this input from the parent's inputs list
            if(input.name == this.name){
                input.loadTree()
                this.value = input.value
                this.output.value = input.value
            }
        })
        return this.value
    }
    
    /**
     * Returns the current value being output
     */ 
    getOutput(){
        return this.output.getValue()
    }
    
}

import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables'

/**
 * This class creates the intersection atom.
 */
export default class Intersection extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        this.addIO('input', 'geometry1', this, 'geometry', '')
        this.addIO('input', 'geometry2', this, 'geometry', '')
        this.addIO('output', 'geometry', this, 'geometry', '')
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Intersection'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Intersection'
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "The space shared by two shapes."
        
        this.setValues(values)
    }


    /**
     * Draw the rectangle atom & icon.
     */ 
    draw(){

        super.draw() //Super call to draw the rest

        const xInPixels = GlobalVariables.widthToPixels(this.x)
        const yInPixels = GlobalVariables.heightToPixels(this.y)
        const radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/4, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/4, yInPixels - radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/4, yInPixels - radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/4, yInPixels)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.fill()
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.lineJoin = "round"
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

    }
    
    /**
     * Grab the input geometries and pass them to a worker thread for computation.
     */ 
    updateValue(){
        try{
            const path1 = this.findIOValue('geometry1')
            const path2 = this.findIOValue('geometry2')
            const values = { key: "intersection",readPath1: path1, readPath2: path2, writePath: this.path }
            
            this.basicThreadValueProcessing(values)
        }catch(err){this.setAlert(err)}
    }
}

import Atom from '../prototypes/atom.js'
import Connector from '../prototypes/connector.js'
import GlobalVariables from '../globalvariables.js'
//import saveAs from '../lib/FileSaver.js'
import { extractBomTags } from '../BOM.js'

/**
 * This class creates the Molecule atom.
 */
export default class Molecule extends Atom{
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /** 
         * A list of all of the atoms within this Molecule which should be drawn on the screen as objects.
         * @type {array}
         */
        this.nodesOnTheScreen = []
        /** 
         * An array of the molecules inputs. Is this not inherited from atom?
         * @type {array}
         */
        this.inputs = []
        /** 
         * This atom's type
         * @type {string}
         */
        this.name = 'Molecule'
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Molecules provide an organizational structure to contain atoms. Double click on a molecule to enter it. Use the up arrow in the upper right hand corner of the screen to go up one level."
        /** 
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Molecule'
        /** 
         * The color for the middle dot in the molecule
         * @type {string}
         */
        this.centerColor = '#949294'
        /** 
         * A flag to indicate if this molecule is the top level molecule.
         * @type {boolean}
         */
        this.topLevel = false
        /** 
         * A flag to indicate if this molecule should simplify it's output.
         * @type {boolean}
         */
        this.simplify = false
        /** 
         * A flag to indicate if this molecule is currently processing.
         * @type {boolean}
         */
        this.processing = false //Should be pulled from atom. Docs made me put this here
        /** 
         * A list of things which should be displayed on the the top level sideBar when in toplevel mode.
         * @type {array}
         */
        this.runModeSidebarAdditions = []
        
        /** 
         * The total number of atoms contained in this molecule
         * @type {integer}
         */
        this.totalAtomCount = 1
        /** 
         * The total number of atoms contained in this molecule which are waiting to process
         * @type {integer}
         */
        this.toProcess = 0
        /**
         * A flag to indicate if this molecule was waiting propagation. If it is it will take place
         *the next time we go up one level.
         * @type {number}
         */
        this.awaitingPropagationFlag = false
        
        this.setValues(values)
        
        //Add the molecule's output
        this.placeAtom({
            parentMolecule: this, 
            x: GlobalVariables.pixelsToWidth(GlobalVariables.canvas.width - 20),
            y: GlobalVariables.pixelsToHeight(GlobalVariables.canvas.height/2),
            parent: this,
            name: 'Output',
            atomType: 'Output',
            uniqueID: GlobalVariables.generateUniqueID()
        }, false)
    }
    
    /**
     * Gives this molecule inputs with the same names as all of it's parent's inputs
     */ 
    copyInputsFromParent(){
        if(this.parent){
            this.parent.nodesOnTheScreen.forEach(node => {
                if(node.atomType == "Input"){
                    this.placeAtom({
                        parentMolecule: this,
                        y: node.y,
                        parent: this,
                        name: node.name,
                        atomType: 'Input',
                        uniqueID: GlobalVariables.generateUniqueID()
                    }, null, GlobalVariables.availableTypes, true)
                }
            })
        } 
    }
    
    /**
     * Add the center dot to the molecule
     */ 
    draw(){
        const percentLoaded = 1-this.toProcess/this.totalAtomCount
        if(this.toProcess > 1){
            this.processing = true
        }
        else{
            this.processing = false
        }
        
        super.draw() //Super call to draw the rest
        
        //draw the circle in the middle
        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = this.centerColor
        GlobalVariables.c.moveTo(GlobalVariables.widthToPixels(this.x), GlobalVariables.heightToPixels(this.y))
        GlobalVariables.c.arc(GlobalVariables.widthToPixels(this.x), GlobalVariables.heightToPixels(this.y), GlobalVariables.widthToPixels(this.radius)/2, 0, percentLoaded*Math.PI * 2, false)
        GlobalVariables.c.closePath()
        GlobalVariables.c.fill()
    }
    
    /**
     * Set the atom's response to a mouse click up. If the atom is moving this makes it stop moving.
     * @param {number} x - The X cordinate of the click
     * @param {number} y - The Y cordinate of the click
     */ 
    clickUp(x,y){
        super.clickUp(x,y)
        GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(atom =>{
            atom.isMoving = false
        })
    }

    /**
     * Handle double clicks by replacing the molecule currently on the screen with this one, esentially diving into it.
     * @param {number} x - The x cordinate of the click
     * @param {number} y - The y cordinate of the click
     */ 
    doubleClick(x,y){
        //returns true if something was done with the click
        x = GlobalVariables.pixelsToWidth(x)
        y = GlobalVariables.pixelsToHeight(y)
        
        var clickProcessed = false
        
        var distFromClick = GlobalVariables.distBetweenPoints(x, this.x, y, this.y)
        
        if (distFromClick < this.radius*2){
            GlobalVariables.currentMolecule = this //set this to be the currently displayed molecule
            GlobalVariables.currentMolecule.backgroundClick()
            /**
            * Deselects Atom
            * @type {boolean}
            */
            this.selected = false
            clickProcessed = true
        }
        
        return clickProcessed 
    }
    
    /**
     * Handle a background click (a click which doesn't land on one of the contained molecules) by deselected everything and displaying a 3D rendering of this molecules output.
     */ 
    backgroundClick(){
        /**
         * Flag that the atom is now selected.
         */
        if(this.selected == false){
            this.selected = true
            this.updateSidebar()
            this.sendToRender()   //This is might need to be removed because it was happening too often during loading
        }
    }

    /**
     * Pushes serialized atoms into array if selected
     */
    copy(){
        this.nodesOnTheScreen.forEach(atom => {
            if(atom.selected){
                GlobalVariables.atomsSelected.push(atom.serialize())
            }
        })
    }
    
    /**
     * Unselect this molecule
     */ 
    deselect(){
        this.selected = false

    }
    
    /**
     * Grab values from the inputs and push them out to the input atoms.
     */ 
    updateValue(targetName){
        
        //Molecules are fully transparent so we don't wait for all of the inputs to begin processing the things inside
        
        //Tell the correct input to update
        this.nodesOnTheScreen.forEach(atom => { //Scan all the input atoms
            if(atom.atomType == 'Input' && atom.name == targetName){  //When there is a match
                atom.updateValue() //Tell that input to update it's value
            }
        })
    }
    
    /**
     * Reads the path of this molecule's output atom
     */ 
    readOutputAtomPath(){
        var returnPath = ""
        this.nodesOnTheScreen.forEach(atom => {
            //If we have found this molecule's output atom use it to update the path here
            if(atom.atomType == "Output"){
                returnPath = atom.path
            }
        })
        return returnPath
    }
    
    /**
     * Sets the atom to wait on coming information. Basically a pass through, but used for molecules
     */ 
    waitOnComingInformation(inputName){
        this.nodesOnTheScreen.forEach( atom => {
            if(atom.name == inputName){
                atom.waitOnComingInformation()
            }
        })
    }
    
    /**
     * Called when this molecules value changes
     */ 
    propogate(){
        //Set the output nodes with type 'geometry' to be the generated code
        if(this.simplify){
            try{
                this.processing = true
                const values = {key: "simplify", readPath: this.readOutputAtomPath(), writePath: this.path}
                window.ask(values).then( () => {
                    this.processing = false
                    this.pushPropogation()
                })
            }catch(err){this.setAlert(err)}
        }
        else{
            this.path = this.readOutputAtomPath()
            this.pushPropogation()
        }
    }
    
    /**
     * Called when this molecules value changes
     */ 
    pushPropogation(){
        if(this != GlobalVariables.currentMolecule){
            this.output.setValue(this.path)
            this.output.ready = true
        }
        else{
            this.awaitingPropagationFlag = true
        }
        
        //If this molecule is selected, send the updated value to the renderer
        if(this.selected){
            this.sendToRender()
        }
    }
    
    /**
     * Walks through each of the atoms in this molecule and begins Propagation from them if they have no inputs to wait for
     */ 
    beginPropagation(force = false){
        
        //Tell every atom inside this molecule to begin Propagation
        this.nodesOnTheScreen.forEach(node => {
            node.beginPropagation(force)
        })
        
        //Generate the simplified path if needed
        if(this.simplify){
            this.generatePath()
        }
    }
    
    /**
     * Walks through each of the atoms in this molecule and takes a census of how many there are and how many are currently waiting to be processed.
     */
    census(){
        this.totalAtomCount = 0
        this.toProcess = 0
        
        this.nodesOnTheScreen.forEach(atom => {
            const newInformation = atom.census()
            this.totalAtomCount = this.totalAtomCount + newInformation[0]
            this.toProcess      = this.toProcess + newInformation[1]
        })
        
        if(this.topLevel && this.selected){
            this.updateSidebar()
        }
        
        return [this.totalAtomCount, this.toProcess]
    }
    
    /**
     * Called when the simplify check box is checked or unchecked.
     */
    setSimplifyFlag(anEvent){
        this.simplify = anEvent.target.checked
        if(this.simplify){
            this.inputPath = this.path
            this.generatePath() //Resets the molecule path to be something unique
        }
        else{  //Changes the path back to be the output atom
            this.nodesOnTheScreen.forEach(atom => {
                if(atom.atomType == "Output"){
                    this.path = atom.loadTree()
                }
            })
        }
        this.propogate()
    }
    
    /**
     * Updates the side bar to display options like 'go to parent' and 'load a different project'. What is displayed depends on if this atom is the top level, and if we are using run mode.
     */ 
    updateSidebar(){
        //Update the side bar to make it possible to change the molecule name
        
        var valueList = super.initializeSideBar() 

        if(!this.topLevel){
            this.createEditableValueListItem(valueList,this,'name','Name', false)
        }
        else if(this.topLevel){
            //If we are the top level molecule 
            this.createSegmentSlider(valueList)
        }
        
        //Display the percent loaded while loading
        const percentLoaded = 100*(1-this.toProcess/this.totalAtomCount)
        if(this.toProcess > 0 && this.topLevel){
            this.createNonEditableValueListItem(valueList,{percentLoaded:percentLoaded.toFixed(0) + "%"},"percentLoaded",'Loading')
        }
        
        //removes 3d view menu on background click
        let viewerBar = document.querySelector('#viewer_bar')
        if(viewerBar && viewerBar.firstChild){
            while (viewerBar.firstChild) {
                viewerBar.removeChild(viewerBar.firstChild)
                viewerBar.setAttribute('style', 'background-color:none;')
            }
        }
        
        
        
        //Add options to set all of the inputs
        this.inputs.forEach(child => {
            if(child.type == 'input' && child.valueType != 'geometry'){
                this.createEditableValueListItem(valueList,child,'value', child.name, true)
            }
        })
        
        //Add the check box to simplify
        this.createCheckbox(valueList,"Simplify output",this.simplify,(anEvent)=>{this.setSimplifyFlag(anEvent)})
        
        //Only bother to generate the bom if we are not currently processing data
        if(this.toProcess == 0){
            this.displaySimpleBOM(valueList)
        }
        
        this.displaySidebarReadme(valueList)
        
        return valueList
        
    }

    /**
     * Creates segment length slider and passes value to Global Variables
     */ 
    createSegmentSlider(valueList){
        //Creates value slider
        var rangeElement = document.createElement('input')
        //Div which contains the entire element
        var div = document.createElement('div')
        div.setAttribute('class', 'slider-container')
        valueList.appendChild(div)
        var rangeLabel = document.createElement('label')
        rangeLabel.textContent = "Display quality/Length of Segments"
        div.appendChild(rangeLabel)
        rangeLabel.appendChild(rangeElement)
        rangeElement.setAttribute('type', 'range')
        rangeElement.setAttribute('min', '.001')
        rangeElement.setAttribute('max', '1')
        rangeElement.setAttribute('step', '.05')
        rangeElement.setAttribute('class', 'slider')
        rangeElement.setAttribute('value', GlobalVariables.circleSegmentSize)
            
        var rangeValueLabel = document.createElement('ul')
        rangeValueLabel.innerHTML= '<li>Export</li><li>Draft</li> '
        rangeValueLabel.setAttribute('class', 'range-labels')
        rangeLabel.appendChild(rangeValueLabel)

        var rangeValue = document.createElement('p')
        rangeValue.textContent = rangeElement.value
        rangeLabel.appendChild(rangeValue)


        //on slider change send value to global variables
        rangeElement.oninput = function() {
            rangeValue.textContent = this.value
            GlobalVariables.circleSegmentSize = this.value
            
        }
        
        rangeElement.addEventListener('mouseup', () => {
            GlobalVariables.topLevelMolecule.refreshCircles()
        })
    }
    
    /**
     * Used to trigger all of the circle atoms within a molecule and all of the molecules within it to update their value. Used when the number of segments changes.
     */ 
    refreshCircles(){
        this.nodesOnTheScreen.forEach(atom => {
            if(atom.atomType == "Circle"){
                atom.updateValue()
            }
            else if(atom.atomType == "Molecule" || atom.atomType == "GitHubMolecule"){
                atom.refreshCircles()
            }
        })
    }
    
    /**
     * Creates a simple BOM list which cannot be edited. The generated element is added to the passed list.
     * @param {object} list - The HTML object to append the created element to.
     */ 
    displaySimpleBOM(list){
        try{
            
            const placementFunction = (bomList) => {
                
                if(bomList.length > 0){
                
                    list.appendChild(document.createElement('br'))
                    list.appendChild(document.createElement('br'))
                    
                    var div = document.createElement('h3')
                    div.setAttribute('style','text-align:center;')
                    list.appendChild(div)
                    var valueText = document.createTextNode('Bill Of Materials')
                    div.appendChild(valueText)
                    
                    var x = document.createElement('HR')
                    list.appendChild(x)
                    
                    bomList.forEach(bomEntry => {
                        this.createNonEditableValueListItem(list,bomEntry,'numberNeeded', bomEntry.BOMitemName, false)
                    })
                }
            }
            
            extractBomTags(this.path, placementFunction)
            
        }catch(err){
            this.setAlert("Unable to read BOM")
        }
    }
    
    /**
     * Creates markdown version of the readme content for this atom in the sidebar
     * @param {object} list - The HTML object to append the created element to.
     */ 
    displaySidebarReadme(list){
        
        
        var readmeContent = ""
        
        this.requestReadme().forEach(item => {
            
            readmeContent = readmeContent + item + "\n\n\n"
        })
        
        if(readmeContent.length > 0){    //If there is anything to say
        
            list.appendChild(document.createElement('br'))
            list.appendChild(document.createElement('br'))

            var div = document.createElement('h3')
            div.setAttribute('style','float:right;')
           
            list.appendChild(div)
            var valueText = document.createTextNode(`- ReadMe`)
            div.appendChild(valueText)
            
            var x = document.createElement('HR')
            x.setAttribute('style','width:100%;')
            list.appendChild(x)
            
            this.createMarkdownListItem(list,readmeContent)
        }
    }
    
    /**
     * Replace the currently displayed molecule with the parent of this molecule...moves the user up one level.
     */
    goToParentMolecule(){
        //Go to the parent molecule if there is one
        if(!this.topLevel){
            this.nodesOnTheScreen.forEach(atom => {
                atom.selected = false
            })
            
            GlobalVariables.currentMolecule = this.parent //set parent this to be the currently displayed molecule
            GlobalVariables.currentMolecule.backgroundClick()
            
            //Push any changes up to the next level if there are any changes waiting in the output
            if(this.awaitingPropagationFlag == true){
                this.propogate()
                this.awaitingPropagationFlag = false
            }
        }
    }
    
    /**
     * Check to see if any of this molecules children have contributions to make to the README file. Children closer to the top left will be applied first. TODO: No contribution should be made if it's just a title.
     */
    requestReadme(){
        var generatedReadme = super.requestReadme()
        
        var sortableAtomsList = this.nodesOnTheScreen
        sortableAtomsList.sort(function(a, b){return GlobalVariables.distBetweenPoints(a.x, 0, a.y, 0)-GlobalVariables.distBetweenPoints(b.x, 0, b.y, 0)})
        
        sortableAtomsList.forEach(atom => {
            generatedReadme = generatedReadme.concat(atom.requestReadme())
        })
        
        //Check to see if any of the children added anything if not, remove the bit we added
        if(generatedReadme[generatedReadme.length - 1] == '## ' + this.name){
            generatedReadme.pop()
        }
        
        return generatedReadme
    }
    
    /**
     * Generates and returns a object represntation of this molecule and all of its children.
     */
    serialize(){
        
        var allAtoms = [] //An array of all the atoms contained in this molecule
        var allConnectors = [] //An array of all the connectors contained in this molecule
        
        this.nodesOnTheScreen.forEach(atom => {
            //Store a representation of the atom
            allAtoms.push(atom.serialize())
            //Store a representation of the atom's connectors
            if(atom.output){
                atom.output.connectors.forEach(connector => {
                    allConnectors.push(connector.serialize())
                })
            }
        })
        
        var thisAsObject = super.serialize()    //Do the atom serialization to create an object, then add all the bits of this one to it
        thisAsObject.topLevel = this.topLevel
        thisAsObject.allAtoms = allAtoms
        thisAsObject.allConnectors = allConnectors
        thisAsObject.fileTypeVersion = 1
        thisAsObject.simplify= this.simplify
        
        return thisAsObject
    }
    
    /**
     * Load the children of this from a JSON representation
     * @param {object} json - A json representation of the molecule
     * @param {object} values - An array of values to apply to this molecule before deserializing it's contents. Used by githubmolecules to set top level correctly
     */
    deserialize(json, values = {}, forceBeginPropagation = false){
        //Find the target molecule in the list
        let promiseArray = []
        
        this.setValues(json) //Grab the values of everything from the passed object
        this.setValues(values) //Over write those values with the passed ones where needed
        
        if(json.allAtoms){
            json.allAtoms.forEach(atom => { //Place the atoms
                const promise = this.placeAtom(atom, false)
                promiseArray.push(promise)
                this.setValues([]) //Call set values again with an empty list to trigger loading of IO values from memory
            })
        }
        
        return Promise.all(promiseArray).then( ()=> { //Once all the atoms are placed we can finish
            
            this.setValues([])//Call set values again with an empty list to trigger loading of IO values from memory
            
            //Place the connectors
            if(json.allConnectors){
                json.allConnectors.forEach(connector => {
                    this.placeConnector(connector)
                })
            }
            
            if(this.topLevel){
                
                GlobalVariables.totalAtomCount = GlobalVariables.numberOfAtomsToLoad
                
                this.census()
                this.loadTree()  //Walks back up the tree from this molecule loading input values from any connected atoms
                
                const splits = this.path.split('/')
                const values = {key: "getPathsList", prefacePath: splits[0]+'/'+splits[1]}
                window.ask(values).then( answer => {
                    
                    GlobalVariables.availablePaths = answer
                    this.beginPropagation(forceBeginPropagation)
                    
                })
                this.backgroundClick()
            }
        })
    }
    
    /**
     * Delete this molecule and everything in it.
     */ 
    deleteNode(backgroundClickAfter = true, deletePath = true, silent = false){
        
        //make a copy of the nodes on the screen array since we will be modifying it
        const copyOfNodesOnTheScreen = [...this.nodesOnTheScreen]
        
        copyOfNodesOnTheScreen.forEach(atom => {
            atom.deleteNode(backgroundClickAfter, deletePath, silent)
        })
        
        super.deleteNode(backgroundClickAfter, deletePath, silent)
    }
    
    /**
     * Triggers the loadTree process from this molecules output
     */ 
    loadTree(){
        //We want to walk the tree from this's output and anything which has nothing coming out of it. Basically all the graph end points.
        
        this.nodesOnTheScreen.forEach(atom => {
            //If we have found this molecule's output atom use it to update the path here
            if(atom.atomType == "Output"){
                this.path = atom.loadTree()
            }
            //If we have found an atom with nothing connected to it
            if(atom.output){
                if(atom.output.connectors.length == 0){
                    atom.loadTree()
                }
            }
        })
        
        this.output.value = this.path
        return this.path
    }
    
    /**
     * Places a new atom inside the molecule
     * @param {object} newAtomObj - An object defining the new atom to be placed
     * @param {array} moleculeList - Only passed if we are placing an instance of Molecule.
     * @param {object} typesList - A dictionary of all of the available types with references to their constructors
     * @param {boolean} unlock - A flag to indicate if this atom should spawn in the unlocked state.
     */
    async placeAtom(newAtomObj, unlock){
        
        GlobalVariables.numberOfAtomsToLoad = GlobalVariables.numberOfAtomsToLoad + 1 //Indicate that one more atom needs to be loaded
        
        try{
            var promise = null
            
            for(var key in GlobalVariables.availableTypes) {
                if (GlobalVariables.availableTypes[key].atomType == newAtomObj.atomType){
                    newAtomObj.parent = this
                    var atom = new GlobalVariables.availableTypes[key].creator(newAtomObj)
                    
                    //reassign the name of the Inputs to preserve linking
                    if(atom.atomType == 'Input' && typeof newAtomObj.name !== 'undefined'){
                        atom.name = newAtomObj.name
                        atom.draw() //The poling happens in draw :roll_eyes:
                    }

                    //If this is a molecule, de-serialize it
                    if(atom.atomType == 'Molecule'){
                        promise = atom.deserialize(newAtomObj)
                    }
                    
                    //If this is a github molecule load it from the web
                    if(atom.atomType == 'GitHubMolecule'){
                        promise = atom.loadProjectByID(atom.projectID)
                        if(unlock){
                            promise.then( ()=> {
                                atom.beginPropagation()
                            })
                        }
                    }
                    
                    //If this is an output, check to make sure there are no existing outputs, and if there are delete the existing one because there can only be one
                    if(atom.atomType == 'Output'){
                        //Check for existing outputs
                        this.nodesOnTheScreen.forEach(atom => {
                            if(atom.atomType == 'Output'){
                                atom.deleteOutputAtom(false) //Remove them
                            }
                        })
                    }
                    
                    //Add the atom to the list to display
                    this.nodesOnTheScreen.push(atom)
                    
                    if(unlock){
                        
                        //Make this molecule spawn with all of it's parent's inputs
                        if(atom.atomType == 'Molecule'){ //Not GitHubMolecule
                            atom.copyInputsFromParent()
                        }
                        
                        //Make begin propagation from an atom when it is placed
                        // if(promise != null){
                        // promise.then( ()=> {
                        // atom.beginPropagation()
                        // })
                        // }
                        // else{
                        // atom.beginPropagation()
                        // }
                        
                        //Fake a click on the newly placed atom
                        const downEvt = new MouseEvent('mousedown', {
                            clientX: atom.x,
                            clientY: atom.y
                        })
                        const upEvt = new MouseEvent('mouseup', {
                            clientX: atom.x,
                            clientY: atom.y
                        })
                        
                        atom.updateValue()
                        
                        document.getElementById('flow-canvas').dispatchEvent(downEvt)
                        document.getElementById('flow-canvas').dispatchEvent(upEvt)
                    }
                }
            }
            return promise
        }catch(err){
            console.warn("Unable to place: " + newAtomObj)
            console.warn(err)
            return Promise.resolve()
        }

    }
    
    /**
     * Places a new connector within the molecule
     * @param {object} connectorObj - An object represntation of the connector specifying its inputs and outputs.
     */
    placeConnector(connectorObj){
        
        var outputAttachmentPoint = false
        var inputAttachmentPoint = false
        
        this.nodesOnTheScreen.forEach(atom => {             //Check each atom on the screen
            if (atom.uniqueID == connectorObj.ap1ID){           //When we have found the output atom
                outputAttachmentPoint = atom.output
            }
            if (atom.uniqueID == connectorObj.ap2ID){           //When we have found the input atom
                atom.inputs.forEach(input => {                  //Check each of its inputs
                    if(input.name == connectorObj.ap2Name){
                        inputAttachmentPoint = input                //Until we find the one with the right name
                    }
                })
            }
        })
        
        if(outputAttachmentPoint && inputAttachmentPoint){             //If we have found the output and input
            new Connector({
                atomType: 'Connector',
                attachmentPoint1: outputAttachmentPoint,
                attachmentPoint2: inputAttachmentPoint,
            })
        }
        else{
            console.warn("Unable to place connector")
        }
    }
    
    /**
     * Sends the output of this molecule to be displayed in the 3D view.
     */
    sendToRender(){
        super.sendToRender()
        if(this.value != null){
            if(this.topLevel){
                this.basicThreadValueProcessing(this.value, "bounding box")
            }
        }
    }
}

import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables.js'
import saveAs from '../lib/FileSaver.js'

import * as svgNest from '../lib/svgnest.js'

/**
 * This class creates the nest atom which lets you download a nested .svg file.
 */
export default class Nest extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Nest'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Nest'
        
        /**
         * This atom's value. Contains the value of the input geometry, not the stl
         * @type {string}
         */
        this.value = null

        /**
         * This atom's height as drawn on the screen
         */
        this.height = 0
        
        this.addIO('input', 'geometry', this, 'geometry', null)
        this.addIO('input', 'spacing', this, 'number', 0.3)
        this.addIO('input', 'curveTolerance', this, 'number', 0.3)
        
        this.setValues(values)

        /**
         * Number of times nesting has been tried
         */
        this.iterations = 0
        /**
         * Boolean to determine wether nesting process is ongoing
         */
        this.isworking = false
        /**
         * Length and width inputs for svg placement
         */
        this.material = {"width": 121,"length":243}
    }
   
    /**
     * Draw the svg atom which has a SVG icon.
     */ 
    draw() {
        super.draw("rect")
        
        let pixelsRadius = GlobalVariables.widthToPixels(this.radius)
        this.height = pixelsRadius * 1.5

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#484848'
        GlobalVariables.c.font = `${pixelsRadius/1.2}px Work Sans Bold`
        GlobalVariables.c.fillText('SVG', GlobalVariables.widthToPixels(this.x- this.radius/1.3), GlobalVariables.heightToPixels(this.y)+this.height/6)
        GlobalVariables.c.fill()
        GlobalVariables.c.closePath()
        
    }
    /**
     * Re excecutes configure function for nesting if values are updated by user
     */ 
    setValue(){
        this.setConfig()
    }

    /**
     * Set the value to be the input geometry, then call super updateValue()
     */ 
    updateValue(){
        try{
            const values = [this.findIOValue('geometry')]
            this.basicThreadValueProcessing(values, "outline")
        }catch(err){this.setAlert(err)}
        //Saves new config values for nesting (Stops any nesting in progress)
        this.setConfig()

    } 
    
    /**
     * Create buttons to start nest, to download the .svg file and checkbox "Part in Part".
     */ 
    updateSidebar(){
        const list = super.updateSidebar()
        this.createEditableValueListItem(list,this.material,'width', 'Width of Material', true, () => this.setConfig())
        this.createEditableValueListItem(list,this.material,'length','Length of Material', true, () => this.setConfig())

        //this.createEditableValueListItem(list,this.BOMitem,'costUSD', 'Price', true, () => this.updateValue())
        this.createCheckbox(list,"Part in Part", false, ()=>{this.setConfig()})
        this.createButton(list, this, "Start Nest", ()=>{this.svgToNest()})
        //remember to disable until svg is nested 
        this.createButton(list, this, "Download SVG", ()=>{this.downloadSvg()})   

        var svgButton = document.getElementById("DownloadSVG-button")
        svgButton.disabled = true
        svgButton.classList.add("disabled")      
    }

    /**
     * Update values for config(). Called when the values on sidebar have been edited.
     */ 
    setConfig() {
        // config = [distance, curve tolerance,rotations, population size, mutation rate, use holes, concave]
        const configKeys = ["spacing","curveTolerance"]
        var c = {"useholes":false,"exploreConcave":false,"rotations":4,"mutationRate":10,"populationSize":10} 
        for(var i=0; i<configKeys.length; i++){
            var key = configKeys[i]
            c[key] = this.findIOValue(key)
        }
        var check1 = document.getElementById("Part in Part")
        if(check1 !== null){
            if (check1.checked){
                c[check1] = true
            }
            else{
                c[check1] = false
               
            }   
        }                  

        window.SvgNest.config(c)
        
        var svgButton = document.getElementById("DownloadSVG-button")  
        if (svgButton !== null){
            svgButton.disabled = true
            svgButton.classList.add("disabled")  
        }
                
        // new configs will invalidate current nest
        if(this.isworking){
            this.stopnest()
        
        }
        return false
    }

    /**
     * Turns geometry values into svg then starts nest and returns nested SVG
     */ 
    svgToNest(){

        //turn into svg
        const values = [this.findIOValue('geometry')]
        
        const computeValue = async (values, key) => {
            try{
                return await GlobalVariables.ask({values: values, key: key})
            }
            catch(err){
                this.setAlert(err)
            }
        }
        var unestedSVG

        computeValue(values, "svg").then(result => {
            if (result != -1 ){
                var decoder = new TextDecoder('utf8')
                unestedSVG = decoder.decode(result)
                
                return unestedSVG
            }else{
                this.setAlert("Unable to compute")
            }
        }).then(result =>{ 
            try{
                var svg = SvgNest.parsesvg(result)
                var display = document.getElementById('select')
                {
                    var wholeSVG = document.createElementNS("http://www.w3.org/2000/svg", "svg")
                    // Copy relevant scaling info
                    wholeSVG.setAttribute('width',this.material["width"])
                    wholeSVG.setAttribute('height',this.material["length"])
                    wholeSVG.setAttribute('viewBox',svg.getAttribute('viewBox'))
                    var rect = document.createElementNS(wholeSVG.namespaceURI,'rect')
                    rect.setAttribute('x', wholeSVG.viewBox.baseVal.x)
                    rect.setAttribute('y', wholeSVG.viewBox.baseVal.x)
                    rect.setAttribute('width', "100%")
                    rect.setAttribute('height', "100%")
                    rect.setAttribute('class', 'fullRect')

                    wholeSVG.appendChild(rect)
                }
                display.innerHTML = ''
                display.appendChild(wholeSVG) // As a default bin in background
                display.appendChild(svg)
            }
            catch(e){
                console.warn(e)
                //message.innerHTML = e;
                //message.className = 'error animated bounce';
                return
            }                   
                        
            //hideSplash();
            //message.className = 'active animated bounce';
            //start.className = 'button start disabled';
                        
            //attachSvgListeners(svg);

            //set the bin to wholeSVG
            this.attachSvgListeners(wholeSVG)
                        
                     
        })
    }
    /**
     * Defines bin which will be used to place parts for nested svg
     */ 
    attachSvgListeners(svg){
        // auto set bin to be whole svg
        for(var i=0; i<svg.childNodes.length; i++){
            var node = svg.childNodes[i]
                    
            if(node.nodeType == 1){
                node.setAttribute("class", "active")
                        
                //if(display.className == 'disabled'){
                //    return;
                //}
                            
                var currentbin = document.querySelector('#select .active')
                if(currentbin){
                    var className = currentbin.getAttribute('class').replace('active', '').trim()
                    if(!className)
                        currentbin.removeAttribute('class')
                    else
                        currentbin.setAttribute('class', className)
                }
                SvgNest.setbin(node) 
                node.setAttribute('class',(node.getAttribute('class') ? node.getAttribute('class')+' ' : '') + 'active')
                            
                //start.className = 'button start animated bounce';
                //message.className = '';
                this.startnest()
                        
            }
        }
    }
    /**
     * Starts nesting, replaces html svg for nested svg
     */ 
    async startnest(){
        // Once started, don't allow this anymore       
        SvgNest.start(this.progress,this.renderSvg)
        //remember to change label so nest can stop
        //startlabel.innerHTML = 'Stop Nest';
        //start.className = 'button spinner';
        //configbutton.className = 'button config disabled';
        //config.className = '';

        var svg = document.querySelector('#select svg')
        if(svg){
            svg.removeAttribute('style')
        }
        this.isworking = true
    }
     
    /**
     * Stops nest and changes flag to not working
     */        
    stopnest(){
        SvgNest.stop()
        //startlabel.innerHTML = 'Start Nest'
        //start.className = 'button start'
        //configbutton.className = 'button config'
                
        this.isworking = false
    }

            
    /**
     * NOT WORKING Defines percentage of nesting and estimates time and iterations
              
    progress(percent, prevpercent = 0){
        
        var transition = percent > prevpercent //? '; transition: width 0.1s' : ''
        //document.getElementById('info_progress').setAttribute('style','width: '+Math.round(percent*100)+'% ' + transition)
        //document.getElementById('info').setAttribute('style','display: block')
                
        prevpercent = percent
                
        var now = new Date().getTime()
        if(startTime && now){
            var diff = now-startTime
            // show a time estimate for long-running placements

            var estimate = (diff/percent)*(1-percent)
            //document.getElementById('info_time').innerHTML = millisecondsToStr(estimate)+' remaining'
            console.log(estimate)        
            if(diff > 5000 && percent < 0.3 && percent > 0.02 && estimate > 10000){
                document.getElementById('info_time').setAttribute('style','display: block')
            }
        }
                
        if(percent > 0.95 || percent < 0.02){
            document.getElementById('info_time').setAttribute('style','display: none')
        }
        if(percent < 0.02){
            startTime = new Date().getTime()
        }
    }
    */

    /**
     * NOT WORKING Defines HTML bit and defines svg list
     */ 
    renderSvg(svglist, efficiency, placed, total){
        //this.iterations++;
                
        //document.getElementById('info_iterations').innerHTML = iterations;     
        //Enable download button if all parts have been placed

        if (placed == total){
            var svgButton = document.getElementById("DownloadSVG-button")
            svgButton.disabled = false
            svgButton.classList.remove("disabled")  
        } 
        if(!svglist || svglist.length == 0){
            return
        }
        var bins = document.getElementById('bins')
        bins.innerHTML = ''
                
        for(var i=0; i<svglist.length; i++){
            if(svglist.length > 2){
                svglist[i].setAttribute('class','grid')
            }
            bins.appendChild(svglist[i])
        }
                
        if(efficiency || efficiency === 0){
            // document.getElementById('info_efficiency').innerHTML = Math.round(efficiency*100);
        }

        //document.getElementById('info_placed').innerHTML = placed+'/'+total;      
        //document.getElementById('info_placement').setAttribute('style','display: block');
        //display.setAttribute('style','display: none');
        //download.className = 'button download animated bounce';
                
    }
            
    /**
     * The function which is called when you press the download button.
     * Stops nest in progress and downloads file to computer. Only enabled if svg has been nested at least once
     */ 
    downloadSvg(){
        //if it is still trying iterations stop nest 
        this.stopnest()
        var bins = document.getElementById('bins')
            
        if(bins.children.length == 0){
            console.warn('No SVG to export')
            return
        }
                
        var svg

        var display = document.getElementById('select')
        svg = display.querySelector('svg')
                
        if(!svg){
            svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg')
        }
                
        svg = svg.cloneNode(false)
                
        // maintain stroke, fill etc of input
        if(SvgNest.style){
            svg.appendChild(SvgNest.style)
        }
                
        var binHeight = parseInt(bins.children[0].getAttribute('height'))
                
        for(var i=0; i<bins.children.length; i++){
            var b = bins.children[i]
            
            var group = document.createElementNS('http://www.w3.org/2000/svg', 'g')
            group.setAttribute('fill', 'none')
            group.setAttribute('stroke-width', '.1')
            group.setAttribute('fill', 'none')
            group.setAttribute('transform', 'translate(0 '+binHeight*1.1*i+')')
            for(var j=0; j<b.children.length; j++){
                group.appendChild(b.children[j].cloneNode(true))
            }
            svg.appendChild(group)
        }
                
        var output
        if(typeof XMLSerializer != 'undefined'){
            output = (new XMLSerializer()).serializeToString(svg)
        }
        else{
            output = svg.outerHTML
        }
        var blob = new Blob([output], {type: "image/svg+xml;charset=utf-8"})
        saveAs(blob, "SVGnest-output.svg")
    }
}

import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables'

/**
 * This class creates the output atom. The goal is that the output atom is fully transparent to the molecule which contains it
 */
export default class Output extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        super (values)
        
        //Add a new output to the current molecule
        if (typeof this.parent !== 'undefined') {
            this.parent.addIO('output', 'Geometry', this.parent, 'geometry', '')
        }
        
        /**
         * This atom's type...not used?
         * @type {string}
         */
        this.type = 'output'
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Output'
        /**
         * This atom's value
         * @type {object}
         */
        this.value = null
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Output'
        /**
         * This atom's height
         * @type {number}
         */
        this.height = 16
        /**
         * This atom's radius
         * @type {number}
         */
        this.radius = 1/75
        /**
         * This atom's path
         * @type {string}
         */
        this.path = "" //Not sure why documentation made me put this hear instead of pulling it from atom
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Connect geometry here to make it available in the next level up. "
        
        this.setValues(values)
        
        this.addIO('input', 'number or geometry', this, 'geometry', undefined)
    }
    
    /**
     * Take the input value of this function and pass it to the parent Molecule to go up one level.
     */ 
    updateValue(){
        if(this.inputs.every(x => x.ready)){
            this.decreaseToProcessCountByOne()
            
            this.path = this.findIOValue('number or geometry')
            //this.parent.path = this.path
            
            this.parent.propogate()  //Propogate passes the updated value on while parent.updateValue is called when one of the molecule inputs changes
        }
    }
    
    /**
     * Sets the parent molecule output to wait on coming information
     */ 
    waitOnComingInformation(){
        this.parent.output.waitOnComingInformation()
    }
    
    /**
     * Sets all the input and output values to match their associated atoms. In this case it sets the path of this and it's parent to be correct.
     */ 
    loadTree(){
        this.path = this.inputs[0].loadTree()
        this.value = this.path
        return this.path
    }
    
    /**
     * Override super delete function to prevent output from being deleted
     */ 
    deleteNode(){
        
    }
    
    /**
     * A function to allow you to still call the delete function if needed.
     */
    deleteOutputAtom(deletePath = true){
        super.deleteNode(false, deletePath)
    }
    
    /**
     * Draw the output shape on the screen.
     */ 
    draw() {

        const xInPixels = GlobalVariables.widthToPixels(this.x)
        const yInPixels = GlobalVariables.heightToPixels(this.y)
        const radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        this.height= radiusInPixels

        //Set colors
        if(this.processing){
            GlobalVariables.c.fillStyle = 'blue'
        }
        else if(this.selected){
            
            GlobalVariables.c.fillStyle = this.selectedColor
            GlobalVariables.c.strokeStyle = this.defaultColor
            /**
             * This background color
             * @type {string}
             */
            this.color = this.selectedColor
            /**
             * This atoms accent color
             * @type {string}
             */
            this.strokeColor = this.defaultColor
        }
        else{
            GlobalVariables.c.fillStyle = this.defaultColor
            GlobalVariables.c.strokeStyle = this.selectedColor
            this.color = this.defaultColor
            this.strokeColor = this.selectedColor
        }
        
        this.inputs.forEach(child => {
            child.draw()       
        })

        GlobalVariables.c.beginPath()
        GlobalVariables.c.textAlign = 'start'
        GlobalVariables.c.fillText(this.name, xInPixels - radiusInPixels, yInPixels - radiusInPixels*1.5)
        GlobalVariables.c.moveTo(xInPixels + radiusInPixels - radiusInPixels*2, yInPixels - this.height)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels -5, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels - radiusInPixels*2, yInPixels + this.height)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels - radiusInPixels*2, yInPixels - this.height)
        GlobalVariables.c.fillStyle = this.color
        GlobalVariables.c.fill()
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.lineJoin = "round"
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()
    }
}

import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables'

/**
 * This class creates the readme atom.
 */
export default class Readme extends Atom{
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.atomType = 'Readme'
        /**
         * The text to appear in the README file
         * @type {string}
         */
        this.readmeText = 'Readme text here'
        /**
         * This atom's type
         * @type {string}
         */
        this.type = 'readme'
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'README'
        /**
         * This atom's radius...probably inherited and can be deleted
         * @type {number}
         */
        this.radius = 1/72
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "A place to put project notes. These will appear in the GitHub readme and in the description of molecules up the chain. Markdown is supported. "

        /**
         * This atom's height as drawn on the screen
         */
        this.height = 10
        
        
        this.setValues(values)
    }
    
    /**
     * Add a place to edit the readme text to the sidebar*/ 
    updateSidebar(){
        var valueList = super.updateSidebar() //call the super function
        this.createEditableValueListItem(valueList,this,'readmeText', 'Notes', false)
    }
    /**
     * Draw the readme atom with // icon.
     */ 
    draw() {
        
        super.draw("rect")

        let pixelsRadius = GlobalVariables.widthToPixels(this.radius)
        this.height = pixelsRadius * 1.5
    
        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#484848'
        GlobalVariables.c.font = `${pixelsRadius*1.5}px Work Sans Bold`
        GlobalVariables.c.fillText('//', GlobalVariables.widthToPixels(this.x- this.radius/2), GlobalVariables.heightToPixels(this.y)+this.height/3)
        GlobalVariables.c.fill()
        GlobalVariables.c.closePath()
        
    }
    /**
     * Update the readme text. Called when the readme text has been edited.
     */ 
    setValue(newText) {
        this.readmeText = newText
    }
    
    /**
     * Provides this molecules contribution to the global Readme
     */ 
    requestReadme(){
        var readBox = `<h3 style="font-size:20px;"><strong>${this.parent.name}:</strong></h3>`.concat(this.readmeText)
        return [readBox]
    }
    
    /**
     * Add the readme text to the information saved for this atom
     */ 
    serialize(values){
        //Save the readme text to the serial stream
        var valuesObj = super.serialize(values)
        
        valuesObj.readmeText = this.readmeText
        
        return valuesObj
        
    }
}

import Atom from '../prototypes/atom'
